import { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import {
  useLazyGetAirportByQueryQuery,
  useSearchFlightsMutation,
} from "../redux/api/airportApi";
import { useDispatch } from "react-redux";
import { setSearchResults } from "../redux/slices/searchSlice";

export default function BookingEngine() {
  const [form, setForm] = useState({
    from: "",
    to: "",
    fromIata: "",
    toIata: "",
    date: "",
    time: "",
    pax: 1,
  });

  const [fromQuery, setFromQuery] = useState("");
  const [toQuery, setToQuery] = useState("");
  const [showFromDropdown, setShowFromDropdown] = useState(false);
  const [showToDropdown, setShowToDropdown] = useState(false);

  const [triggerAirportSearch, { data: airportResults = [] }] =
    useLazyGetAirportByQueryQuery();

  const [searchFlights] = useSearchFlightsMutation();
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const dateRef = useRef(null);
  const timeRef = useRef(null);

  // handle airport typing
  const handleAirportSearch = async (query, type) => {
    if (query.length < 2) return; // avoid too many calls
    const result = await triggerAirportSearch(query);
    if (result?.data) {
      if (type === "from") setShowFromDropdown(true);
      else setShowToDropdown(true);
    }
  };

  const handleSelectAirport = (airport, type) => {
    setForm((prev) => ({
      ...prev,
      [type]: airport.name,
      [`${type}Iata`]: airport.code,
    }));
    if (type === "from") setShowFromDropdown(false);
    else setShowToDropdown(false);
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = {
        from: form.fromIata,
        to: form.toIata,
        date: form.date,
        time: form.time,
        pax: form.pax,
      };

      const result = await searchFlights(payload).unwrap();
      dispatch(setSearchResults({ results: result, form }));
      navigate("/results");
    } catch (err) {
      console.error("Search failed:", err);
    }
  };

  return (
    <div className="booking-engine">
      <div className="booking-tabs">
        <button className="active">Fly Now</button>
        <button>Empty Leg</button>
        <button>Regional</button>
        <button>Pilgrimage</button>
      </div>

      <form id="flynow" className="booking-fields" onSubmit={handleSubmit}>
        {/* FROM / TO */}
        <div className="booking-form-row booking-form-half">
          {/* FROM */}
          <div className="booking-form-col">
            <label>
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-from.png`}
                alt="From"
              />
              From
            </label>
            <input
              type="text"
              value={form.from}
              placeholder="Search departure airport"
              onChange={(e) => {
                setForm({ ...form, from: e.target.value });
                setFromQuery(e.target.value);
                handleAirportSearch(e.target.value, "from");
              }}
              required
            />
            {showFromDropdown && (
              <ul className="airport-suggestions">
                {airportResults
                  ?.filter((a) =>
                    a.name.toLowerCase().includes(fromQuery.toLowerCase())
                  )
                  .slice(0, 8)
                  .map((airport) => (
                    <li
                      key={airport.code}
                      onClick={() => handleSelectAirport(airport, "from")}
                    >
                      {airport.name} ({airport.code}) - {airport.city}
                    </li>
                  ))}
              </ul>
            )}
          </div>

          {/* TO */}
          <div className="booking-form-col">
            <label>
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-to.png`}
                alt="To"
              />
              To
            </label>
            <input
              type="text"
              value={form.to}
              placeholder="Search destination airport"
              onChange={(e) => {
                setForm({ ...form, to: e.target.value });
                setToQuery(e.target.value);
                handleAirportSearch(e.target.value, "to");
              }}
              required
            />
            {showToDropdown && (
              <ul className="airport-suggestions">
                {airportResults
                  ?.filter((a) =>
                    a.name.toLowerCase().includes(toQuery.toLowerCase())
                  )
                  .slice(0, 8)
                  .map((airport) => (
                    <li
                      key={airport.code}
                      onClick={() => handleSelectAirport(airport, "to")}
                    >
                      {airport.name} ({airport.code}) - {airport.city}
                    </li>
                  ))}
              </ul>
            )}
          </div>
        </div>

        {/* DATE / TIME */}
        <div className="booking-form-row booking-form-half">
          <div
            className="booking-form-col clickable-field"
            onClick={() => dateRef.current?.showPicker()}
          >
            <label>
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-date.png`}
                alt="Date"
              />
              Date
            </label>
            <input
              type="date"
              name="date"
              ref={dateRef}
              value={form.date}
              onChange={handleChange}
              min={new Date().toISOString().split("T")[0]}
              required
            />
          </div>
          <div
            className="booking-form-col clickable-field"
            onClick={() => timeRef.current?.showPicker()}
          >
            <label>
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-time.png`}
                alt="Time"
              />
              Time
            </label>
            <input
              type="time"
              name="time"
              ref={timeRef}
              value={form.time}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        {/* PAX */}
        <div className="booking-form-row booking-form-full">
          <div className="booking-form-col">
            <label>
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-user.png`}
                alt="Passengers"
              />
              Passengers
            </label>
            <input
              type="number"
              name="pax"
              min="1"
              value={form.pax}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        {/* SUBMIT */}
        <div className="booking-form-button">
          <input type="submit" className="btn btn-fill" value="Search" />
        </div>
      </form>
    </div>
  );
}
