import { useState } from "react";
import toast from "react-hot-toast";
import axios from "../../utils/axios";
import PublicLayout from "../../components/layout/PublicLayout";

/**
 * Upload file to AWS S3 using presigned URL
 */
const uploadToS3 = async (file, folder) => {
  // 1. Get presigned URL from backend
  const { data } = await axios.get(
    `/s3/presigned-url?fileType=${encodeURIComponent(
      file.type
    )}&folder=${encodeURIComponent(folder)}`
  );

  // 2. Upload file directly to S3
  await fetch(data.uploadUrl, {
    method: "PUT",
    headers: {
      "Content-Type": file.type,
    },
    body: file,
  });

  // 3. Return public S3 URL
  return data.fileUrl;
};

const ModelRegister = () => {
  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    stageName: "",
    gender: "Female",
    dateOfBirth: "",
    email: "",
    phone: "",
    country: "",
    state: "",
    city: "",
    heightCm: "",
    weightKg: "",
    bustCm: "",
    waistCm: "",
    hipsCm: "",
    chestCm: "",
    shoeSize: "",
    eyeColor: "",
    hairColor: "",
    skinTone: "",
    bodyType: "Average",
    tattoos: false,
    piercings: false,
    experienceLevel: "Beginner",
    languages: "",
    skills: "",
    availableFor: "",
    travelWillingness: "Yes",
    expectedPayPerDay: "",
  });

  const [profileImage, setProfileImage] = useState(null);
  const [portfolioImages, setPortfolioImages] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleProfileImage = (e) => {
    setProfileImage(e.target.files[0]);
  };

  const handlePortfolioImages = (e) => {
    setPortfolioImages(Array.from(e.target.files));
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((p) => ({
      ...p,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const submit = async (e) => {
    e.preventDefault();

    try {
      setLoading(true);

      // Upload images to S3
      let profileImageUrl = "";
      let portfolioUrls = [];

      if (profileImage) {
        profileImageUrl = await uploadToS3(profileImage, "models/profile");
      }

      for (const img of portfolioImages) {
        const url = await uploadToS3(img, "models/portfolio");
        portfolioUrls.push(url);
      }

      // Prepare payload
      const payload = {
        ...form,
        languages: form.languages
          ? form.languages.split(",").map((l) => l.trim())
          : [],
        skills: form.skills ? form.skills.split(",").map((s) => s.trim()) : [],
        availableFor: form.availableFor
          ? form.availableFor.split(",").map((a) => a.trim())
          : [],
        profileImage: profileImageUrl,
        portfolio: portfolioUrls,
      };

      // Submit form
      await axios.post("/models/register", payload);

      toast.success("Profile submitted. Await admin approval.");
    } catch (err) {
      toast.error(err.response?.data?.message || "Registration failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <PublicLayout>
      <section className="container py-5">
        <div className="text-center mb-5">
          <h1 className="fw-bold">Create Your Model Profile</h1>
          <p className="text-muted">
            Join Dubai’s premium escort directory. Free listing — upgrade
            anytime.
          </p>
        </div>

        <form onSubmit={submit} className="mx-auto" style={{ maxWidth: 1100 }}>
          {/* BASIC INFO */}
          <div className="card shadow-sm mb-4">
            <div className="card-header bg-white fw-semibold">
              Basic Information
            </div>
            <div className="card-body row g-3">
              <div className="col-md-4">
                <label>First Name *</label>
                <input
                  name="firstName"
                  required
                  className="form-control"
                  onChange={handleChange}
                />
              </div>
              <div className="col-md-4">
                <label>Last Name</label>
                <input
                  name="lastName"
                  className="form-control"
                  onChange={handleChange}
                />
              </div>
              <div className="col-md-4">
                <label>Stage Name</label>
                <input
                  name="stageName"
                  className="form-control"
                  onChange={handleChange}
                />
              </div>
            </div>
          </div>

          {/* MEDIA */}
          <div className="card shadow-sm mb-4">
            <div className="card-header bg-white fw-semibold">
              Photos & Media
            </div>
            <div className="card-body row g-4">
              <div className="col-md-6">
                <label className="fw-semibold">Profile Image</label>
                <input
                  type="file"
                  accept="image/*"
                  className="form-control"
                  onChange={handleProfileImage}
                />
                {profileImage && (
                  <img
                    src={URL.createObjectURL(profileImage)}
                    alt="Profile Preview"
                    className="mt-2 rounded"
                    style={{ width: 120, height: 120, objectFit: "cover" }}
                  />
                )}
              </div>

              <div className="col-md-6">
                <label className="fw-semibold">Portfolio Images</label>
                <input
                  type="file"
                  accept="image/*"
                  multiple
                  className="form-control"
                  onChange={handlePortfolioImages}
                />
                <div className="d-flex gap-2 flex-wrap mt-2">
                  {portfolioImages.map((img, i) => (
                    <img
                      key={i}
                      src={URL.createObjectURL(img)}
                      alt="Portfolio"
                      className="rounded"
                      style={{ width: 90, height: 90, objectFit: "cover" }}
                    />
                  ))}
                </div>
              </div>
            </div>
          </div>

          {/* CTA */}
          <div className="text-center">
            <button className="btn btn-dark btn-lg px-5" disabled={loading}>
              {loading ? "Submitting..." : "Submit Profile"}
            </button>
            <p className="text-muted mt-2">
              Free listing • Admin verification • Upgrade anytime
            </p>
          </div>
        </form>
      </section>
    </PublicLayout>
  );
};

export default ModelRegister;
