import React, { useState, useEffect } from "react";
import { useGetUsersQuery } from "../../redux/api/adminApi";
import AddUserForm from "./AddUserForm";
import toast from "react-hot-toast";
import DataTable from "react-data-table-component";

const UserList = () => {
  const { data, isLoading, error, refetch } = useGetUsersQuery();
  const [showAddForm, setShowAddForm] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredUsers, setFilteredUsers] = useState([]);

  useEffect(() => {
    if (data?.users) {
      setFilteredUsers(
        data.users.filter((user) => {
          const term = searchTerm.toLowerCase();
          return (
            user.name?.toLowerCase().includes(term) ||
            user.email?.toLowerCase().includes(term) ||
            user.role?.toLowerCase().includes(term)
          );
        })
      );
    }
  }, [data, searchTerm]);

  if (error) {
    toast.error("Failed to load users");
    return null;
  }

  const columns = [
    {
      name: "Name",
      selector: (row) => row.name || "N/A",
      sortable: true,
    },
    {
      name: "Email",
      selector: (row) => row.email || "N/A",
      sortable: true,
    },
    {
      name: "Current Role",
      selector: (row) => row.role || "N/A",
      sortable: true,
    },
    {
      name: "Actions",
      cell: (row) => (
        <button
          className="btn btn-sm btn-warning"
          onClick={() => {
            // Ideally, open a modal or form to edit the role
            toast(`Edit role for ${row.name}`);
          }}
        >
          Change Role
        </button>
      ),
      ignoreRowClick: true,
      allowOverflow: true,
      button: true,
    },
  ];

  return (
    <div className="container mt-4">
      <h3>User Role Management</h3>

      <button
        className="btn btn-primary mb-3"
        onClick={() => setShowAddForm(!showAddForm)}
      >
        {showAddForm ? "Hide Add Form" : "Add New User"}
      </button>

      {showAddForm && (
        <AddUserForm onClose={() => setShowAddForm(false)} refetch={refetch} />
      )}

      <input
        type="text"
        className="form-control mb-3"
        placeholder="Search users..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <DataTable
        columns={columns}
        data={filteredUsers}
        progressPending={isLoading}
        pagination
        highlightOnHover
        striped
        noDataComponent="No users found"
      />
    </div>
  );
};

export default UserList;
