import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import {
  useUpdateProfileMutation,
  useGetMeQuery,
} from "../../redux/api/userApi";
import { useLazyLogoutQuery } from "../../redux/api/authApi";
import { toast } from "react-toastify";

const UserProfile = () => {
  // RTK Query hooks
  const { data: userData, refetch } = useGetMeQuery();
  const [updateProfile, { isLoading: updating }] = useUpdateProfileMutation();
  const [triggerLogout] = useLazyLogoutQuery();

  // Redux state
  const { userInfo } = useSelector((state) => state.auth);

  // Form states
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [avatar, setAvatar] = useState(null);
  const [avatarPreview, setAvatarPreview] = useState("/default-avatar.png");

  // Populate form on load
  useEffect(() => {
    const user = userData?.user || userInfo;
    if (user) {
      setName(user.name || "");
      setEmail(user.email || "");
      setPhone(user.phone || "");
      setAvatarPreview(user.avatar?.url || "/default-avatar.png");
    }
  }, [userData, userInfo]);

  // ✅ Handle avatar change
  const onAvatarChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setAvatar(file);
      setAvatarPreview(URL.createObjectURL(file));
    }
  };

  // ✅ Handle profile update
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append("name", name);
      formData.append("email", email);
      formData.append("phone", phone);
      if (avatar) formData.append("avatar", avatar);

      const res = await updateProfile(formData).unwrap();
      if (res.success) {
        toast.success("Profile updated successfully!");
        refetch();
      }
    } catch (err) {
      toast.error(err?.data?.message || "Failed to update profile");
    }
  };

  // ✅ Handle logout
  const handleLogout = async () => {
    try {
      await triggerLogout().unwrap();
      toast.success("Signed out successfully!");
      window.location.href = "/login"; // redirect after logout
    } catch (err) {
      toast.error("Failed to logout");
    }
  };

  return (
    <div className="profile-container max-w-2xl mx-auto bg-white rounded-2xl shadow p-6 mt-10">
      <h2 className="text-2xl font-semibold mb-6 text-center">My Profile</h2>

      <form
        onSubmit={handleSubmit}
        encType="multipart/form-data"
        className="space-y-4"
      >
        {/* Avatar Section */}
        <div className="flex flex-col items-center mb-4">
          <div className="relative">
            <img
              src={avatarPreview}
              alt="Avatar"
              className="w-24 h-24 rounded-full border-2 border-gray-300 object-cover"
            />
            <label className="absolute bottom-0 right-0 bg-blue-600 text-white text-xs px-2 py-1 rounded-full cursor-pointer">
              Change
              <input
                type="file"
                accept="image/*"
                className="hidden"
                onChange={onAvatarChange}
              />
            </label>
          </div>
        </div>

        {/* Name */}
        <div>
          <label className="block text-sm font-medium text-gray-700">
            Name
          </label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="mt-1 block w-full border rounded-md p-2 focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>

        {/* Email */}
        <div>
          <label className="block text-sm font-medium text-gray-700">
            Email
          </label>
          <input
            type="email"
            value={email}
            readOnly
            className="mt-1 block w-full border rounded-md p-2 bg-gray-100 cursor-not-allowed"
          />
        </div>

        {/* Phone */}
        <div>
          <label className="block text-sm font-medium text-gray-700">
            Phone
          </label>
          <input
            type="text"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            className="mt-1 block w-full border rounded-md p-2"
          />
        </div>

        {/* Role */}
        <div>
          <label className="block text-sm font-medium text-gray-700">
            Role
          </label>
          <input
            type="text"
            value={userData?.user?.role?.name || "User"}
            className="mt-1 block w-full border rounded-md p-2 bg-gray-100 cursor-not-allowed"
            readOnly
          />
        </div>

        {/* Buttons */}
        <div className="flex justify-between mt-6">
          <button
            type="button"
            onClick={handleLogout}
            className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
          >
            Sign Out
          </button>
          <button
            type="submit"
            disabled={updating}
            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            {updating ? "Updating..." : "Update Profile"}
          </button>
        </div>
      </form>
    </div>
  );
};

export default UserProfile;
