import { Link } from "react-router-dom";

const ModelCard = ({ model }) => {
  const age = model.dateOfBirth
    ? new Date().getFullYear() - new Date(model.dateOfBirth).getFullYear()
    : "";

  return (
    <article className="card">
      <Link to={`/model/${model._id}`} className="card-img">
        {model.profileImage?.url ? (
          <img src={model.profileImage.url} alt={model.firstName} />
        ) : (
          <div className="card-img-placeholder">
            <span>[Model photo]</span>
          </div>
        )}
      </Link>

      <div className="card-body">
        <div className="card-header-row">
          <h3 className="card-name">{model.stageName || model.firstName}</h3>
          <span className="card-age">{age} yrs</span>
          <span className="card-location">{model.city}</span>
        </div>

        <p className="card-labels">
          {model.experienceLevel && (
            <span className="pill">{model.experienceLevel}</span>
          )}
        </p>

        {model.expectedPayPerDay && (
          <p className="card-meta">From ${model.expectedPayPerDay}</p>
        )}
      </div>
    </article>
  );
};

export default ModelCard;
