import React, { useState, useEffect } from "react";
import toast from "react-hot-toast";
import {
  useGetAirportsQuery,
  useCreateAirportMutation,
} from "../../redux/api/airportApi";
import { useUpdateAirportMutation } from "../../redux/api/airportApi";

const AirportForm = ({ editingAirport, onSuccess }) => {
  const [form, setForm] = useState({
    name: "",
    code: "",
    city: "",
    country: "",
  });

  const { data: airports } = useGetAirportsQuery();
  const [addAirport] = useCreateAirportMutation();
  const [updateAirport] = useUpdateAirportMutation();

  useEffect(() => {
    if (editingAirport) {
      setForm({
        name: editingAirport.name,
        code: editingAirport.code,
        city: editingAirport.city,
        country: editingAirport.country,
      });
    }
  }, [editingAirport]);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingAirport) {
        await updateAirport({ id: editingAirport._id, data: form }).unwrap();
        toast.success("Airport updated");
      } else {
        await addAirport(form).unwrap();
        toast.success("Airport added");
      }
      onSuccess();
    } catch (err) {
      toast.error("Error saving airport");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="border p-3 rounded mt-4">
      <h5>{editingAirport ? "Edit" : "Add"} Airport</h5>
      <input
        type="text"
        className="form-control mb-2"
        name="name"
        placeholder="Name"
        value={form.name}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        className="form-control mb-2"
        name="code"
        placeholder="Code"
        value={form.code}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        className="form-control mb-2"
        name="city"
        placeholder="City"
        value={form.city}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        className="form-control mb-2"
        name="country"
        placeholder="Country"
        value={form.country}
        onChange={handleChange}
        required
      />
      <button type="submit" className="btn btn-primary">
        {editingAirport ? "Update" : "Add"} Airport
      </button>
    </form>
  );
};
export default AirportForm;
