import React, { useState, useMemo } from "react";
import DataTable from "react-data-table-component";
import {
  useGetBookingsQuery,
  useDeleteBookingMutation,
} from "../../redux/api/bookingApi";
import toast from "react-hot-toast";
import AddBookingForm from "./AddBookingForm";
import AddPaymentForm from "./AddPaymentForm";
import BookingViewModal from "./BookingViewModal";
import { airports } from "airports-json"; // ✅ import airport dataset
import BookingHistoryModal from "./BookingHistoryModal";
import { useLocation, useNavigate } from "react-router-dom";

const BookingList = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const params = new URLSearchParams(location.search);
  const autoSearch = params.get("search"); // booking code or email or name
  const autoOpen = params.get("open"); // "true"

  const [searchTerm, setSearchTerm] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [editBooking, setEditBooking] = useState(null);
  const [showPayment, setShowPayment] = useState(false);
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [viewBooking, setViewBooking] = useState(null);

  const { data, isLoading, refetch } = useGetBookingsQuery();
  const [deleteBooking] = useDeleteBookingMutation();
  const bookings = data?.bookings || [];

  useEffect(() => {
    if (autoSearch) {
      setSearchTerm(autoSearch.toLowerCase());
    }
  }, [autoSearch]);

  useEffect(() => {
    if (autoOpen === "true" && bookings.length > 0) {
      const found = bookings.find(
        (b) =>
          b.bookingCode === autoSearch ||
          b.user?.email?.toLowerCase() === autoSearch?.toLowerCase() ||
          b.user?.name?.toLowerCase() === autoSearch?.toLowerCase()
      );

      if (found) {
        setEditBooking(found);
        setShowForm(true);
        setShowPayment(false);

        // Optional: scroll into view
        setTimeout(() => {
          const row = document.getElementById(`booking-${found._id}`);
          row?.scrollIntoView({ behavior: "smooth", block: "center" });
        }, 300);
      }
    }
  }, [autoOpen, autoSearch, bookings]);

  // ✅ Utility: find airport by IATA code
  const getAirportLabel = (iata) => {
    if (!iata) return "-";
    const airport = airports.find((a) => a.iata_code === iata);
    if (airport) {
      return `${airport.iata_code}`;
    }
    return iata; // fallback if not found
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure?")) {
      try {
        await deleteBooking(id).unwrap();
        toast.success("Deleted successfully");
        refetch();
      } catch (err) {
        toast.error("Delete failed");
      }
    }
  };

  const filteredBookings = useMemo(() => {
    if (!searchTerm.trim()) return bookings;
    return bookings.filter((b) => {
      const target =
        `${b.user?.name} ${b.user?.email} ${b.user?.mobile} ${b.bookingType} ${b.status} ${b.fromPlace} ${b.toPlace}`.toLowerCase();
      return target.includes(searchTerm.toLowerCase());
    });
  }, [searchTerm, bookings]);

  const columns = [
    {
      name: "Booking ID",
      selector: (row) => row.bookingCode || "-",
      sortable: true,
      center: true,
    },
    {
      name: "Traveler",
      selector: (row) => row.user?.name || "-",
      sortable: true,
      center: true,
    },
    {
      name: "Flight Type",
      selector: (row) => row.flightType?.name || "-",
      sortable: true,
      center: true,
    },
    {
      name: "Booking Type",
      selector: (row) => row.bookingType,
      sortable: true,
      cell: (row) => (
        <span
          className={`badge bg-${
            row.bookingType === "normal" ? "primary" : "secondary"
          }`}
        >
          {row.bookingType}
        </span>
      ),
      center: true,
    },
    {
      name: "From–To",
      selector: (row) =>
        `${getAirportLabel(row.fromPlace)} → ${getAirportLabel(row.toPlace)}`,
      center: true,
    },
    {
      name: "Date",
      selector: (row) => row.travelDate || "-",
      sortable: true,
      center: true,
    },
    {
      name: "Time",
      selector: (row) => row.travelTime || "-",
      sortable: true,
      center: true,
    },
    {
      name: "Status",
      cell: (row) => (
        <span
          className={`badge bg-${
            row.status === "confirmed"
              ? "success"
              : row.status === "cancelled"
              ? "danger"
              : "warning"
          }`}
        >
          {row.status}
        </span>
      ),
      center: true,
    },
    {
      name: "Actions",
      cell: (row) => (
        <div className="dropdown position-static">
          <button
            className="btn btn-sm btn-secondary dropdown-toggle"
            type="button"
            data-bs-toggle="dropdown"
            aria-expanded="false"
            style={{ zIndex: 99999 }}
          >
            Actions
          </button>

          <ul
            className="dropdown-menu position-fixed"
            style={{ zIndex: 99999 }}
          >
            <li>
              <button
                className="dropdown-item"
                onClick={() => setViewBooking(row)}
              >
                View
              </button>
            </li>
            <li>
              <button
                className="dropdown-item"
                onClick={() => {
                  setEditBooking(row);
                  setShowForm(true);
                  setShowPayment(false);
                }}
              >
                Edit
              </button>
            </li>
            <li>
              <button
                className="dropdown-item"
                onClick={() => handleDelete(row._id)}
              >
                Delete
              </button>
            </li>
            <li>
              <button
                className="dropdown-item"
                onClick={() => {
                  setSelectedBooking(row);
                  setShowForm(false);
                  setShowPayment(true);
                }}
              >
                Add Payment
              </button>
            </li>
            <li>
              <button
                className="dropdown-item"
                onClick={() => setHistoryBooking(row)}
              >
                View History
              </button>
            </li>
          </ul>
        </div>
      ),
      ignoreRowClick: true,
      allowoverflow: true,
      button: true,
      center: true,
    },
  ];

  // const columns = [
  //   {
  //     name: "Traveler",
