import React, { useState } from "react";
import {
  useGetFlightTypesQuery,
  useDeleteFlightTypeMutation,
} from "../../redux/api/flightTypeApi";
import toast from "react-hot-toast";
import FlightTypeForm from "./FlightTypeForm";

const FlightTypeList = () => {
  const { data, isLoading, refetch } = useGetFlightTypesQuery();
  const [deleteFlightType] = useDeleteFlightTypeMutation();
  const [editFlight, setEditFlight] = useState(null);

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this flight type?")) {
      await deleteFlightType(id);
      toast.success("Deleted successfully");
    }
  };

  return (
    <div className="container mt-4">
      <h3>Manage Flight Types</h3>
      <FlightTypeForm
        selectedFlight={editFlight}
        onSuccess={() => {
          setEditFlight(null);
          refetch();
        }}
      />
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>Name</th>
              <th>Capacity</th>
              <th>Description</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {data?.flights?.map((flight) => (
              <tr key={flight._id}>
                <td>{flight.name}</td>
                <td>{flight.capacity}</td>
                <td>{flight.description}</td>
                <td>{flight.status ? "Active" : "Inactive"}</td>
                <td>
                  <button
                    className="btn btn-sm btn-warning me-2"
                    onClick={() => setEditFlight(flight)}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn-sm btn-danger"
                    onClick={() => handleDelete(flight._id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default FlightTypeList;
