import React, { useState } from "react";
import DataTable from "react-data-table-component";
import {
  useDeleteBookingMutation,
  useGetBookingsQuery,
} from "../../redux/api/bookingApi";
import { Link, useSearchParams } from "react-router-dom";
import AddBookingForm from "./AddBookingForm";
import toast from "react-hot-toast";
import AddPaymentForm from "./AddPaymentForm";
import CustomPagination from "../layout/CustomPagination";
import SearchBar from "../layout/SearchBar";

const BookingList = () => {
  const [showForm, setShowForm] = useState(false);
  const [editBooking, setEditBooking] = useState(null);
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [showPayment, setShowPayment] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [searchParams, setSearchParams] = useSearchParams();

  const page = searchParams.get("page") || 1;
  const search = searchParams.get("search") || "";

  const params = { page: Number(page) };
  if (search) params.search = search;
  if (searchParams.get("sort")) {
    params.sort = searchParams.get("sort");
  }

  const { data, isLoading, refetch } = useGetBookingsQuery(params);
  const bookings = data?.bookings || [];
  const [deleteBooking] = useDeleteBookingMutation();

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this booking?")) {
      try {
        await deleteBooking(id).unwrap();
        toast.success("Booking deleted");
        refetch();
      } catch (err) {
        toast.error("Failed to delete booking");
      }
    }
  };

  const handleEdit = (booking) => {
    setEditBooking(booking);
    setShowForm(true);
    setShowPayment(false);
  };

  const handleCloseForm = () => {
    setShowForm(false);
    setEditBooking(null);
  };

  const handlePaymentClick = (booking) => {
    setSelectedBooking(booking);
    setShowForm(false);
    setShowPayment(true);
    setTimeout(() => {
      const paymentForm = document.getElementById("payment-form");
      if (paymentForm) {
        paymentForm.scrollIntoView({ behavior: "smooth", block: "start" });
      }
    }, 100);
  };

  const handleSortChange = (column, sortDirection) => {
    const newSort =
      sortDirection === "desc" ? `-${column.selector}` : column.selector;
    searchParams.set("sort", newSort);
    searchParams.set("page", 1);
    setSearchParams(searchParams);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    const params = new URLSearchParams(searchParams);
    if (searchTerm.trim()) {
      params.set("search", searchTerm.trim());
      params.set("page", 1);
    } else {
      params.delete("search");
    }
    setSearchParams(params);
  };

  const columns = [
    {
      name: "ID",
      selector: (row) => row._id.slice(-5),
      sortable: true,
    },
    {
      name: "Traveler",
      selector: (row) => row.user?.name,
      sortable: true,
    },
    {
      name: "Email",
      selector: (row) => row.user?.email,
      sortable: true,
    },
    {
      name: "Mobile",
      selector: (row) => row.user?.mobile,
    },
    {
      name: "From–To",
      cell: (row) => `${row.fromPlace} → ${row.toPlace}`,
    },
    {
      name: "Date/Time",
      cell: (row) => `${row.travelDate} ${row.travelTime}`,
    },
    {
      name: "Status",
      cell: (row) => (
        <span
          className={`badge bg-${
            row.status === "confirmed"
              ? "success"
              : row.status === "cancelled"
              ? "danger"
              : "warning"
          }`}
        >
          {row.status}
        </span>
      ),
    },
    {
      name: "Actions",
      cell: (row) => (
        <div className="dropdown">
          <button
            className="btn btn-sm btn-secondary dropdown-toggle"
            type="button"
            data-bs-toggle="dropdown"
          >
            Actions
          </button>
          <ul className="dropdown-menu">
            <li>
              <Link to={`/admin/bookings/${row._id}`} className="dropdown-item">
                View
              </Link>
            </li>
            <li>
              <button className="dropdown-item" onClick={() => handleEdit(row)}>
                Edit
              </button>
            </li>
            <li>
              <button
                className="dropdown-item"
                onClick={() => handleDelete(row._id)}
              >
                Delete
              </button>
            </li>
            <li>
              <button
                className="dropdown-item"
                onClick={() => handlePaymentClick(row)}
              >
                Add Payment
              </button>
            </li>
          </ul>
        </div>
      ),
      ignoreRowClick: true,
      allowOverflow: true,
      button: true,
    },
  ];

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>Bookings</h2>
        <button
          className="btn btn-primary"
          onClick={() => {
            setEditBooking(null);
            setShowForm(!showForm);
            setShowPayment(false);
          }}
        >
          {showForm ? "Close Form" : "Add Booking"}
        </button>
      </div>

      {showForm && (
        <AddBookingForm
          setShowForm={handleCloseForm}
          refetch={refetch}
          booking={editBooking}
        />
      )}

      {showPayment && selectedBooking && (
        <AddPaymentForm
          booking={selectedBooking}
          refetch={refetch}
          onClose={() => setShowPayment(false)}
        />
      )}

      <SearchBar
        value={searchTerm}
        onChange={setSearchTerm}
        onSubmit={handleSearch}
      />

      <div className="mt-4">
        <DataTable
          columns={columns}
          data={bookings}
          progressPending={isLoading}
          highlightOnHover
          striped
          pagination={false}
          onSort={handleSortChange}
          sortServer
        />
        <CustomPagination
          resPerPage={data?.resPerPage || 10}
          totalResults={data?.filteredBookingsCount || 0}
        />
      </div>
    </div>
  );
};

export default BookingList;
