import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function Masters() {
  const { type } = useParams(); // ✅ THIS IS THE FIX

  const [items, setItems] = useState([]);
  const [label, setLabel] = useState("");

  const load = async () => {
    const res = await axios.get(`/api/v1/admin/masters?type=${type}`);
    setItems(res.data.masters);
  };

  useEffect(() => {
    if (type) load();
  }, [type]);

  const add = async () => {
    if (!label.trim()) return;

    await axios.post("/api/v1/admin/masters", {
      type, // ✅ now defined
      label,
      value: label.toLowerCase().replace(/\s+/g, "_"),
    });

    setLabel("");
    load();
  };

  return (
    <div className="card p-4">
      <h4 className="text-capitalize">{type} Master</h4>

      <input
        value={label}
        onChange={(e) => setLabel(e.target.value)}
        placeholder={`Add ${type}`}
        className="form-control mb-2"
      />

      <button onClick={add} className="btn btn-primary mb-3">
        Add
      </button>

      <ul className="list-group">
        {items.map((i) => (
          <li key={i._id} className="list-group-item">
            {i.label}
          </li>
        ))}
      </ul>
    </div>
  );
}
