import React, { useState, useEffect } from "react";

export default function EmptyLegModal({ legs, onClose }) {
  const [updated, setUpdated] = useState(
    JSON.parse(JSON.stringify(legs || []))
  );

  useEffect(() => {
    document.body.style.overflow = "hidden"; // lock background scroll
    return () => {
      document.body.style.overflow = "auto";
    };
  }, []);

  const handlePriceChange = (index, value) => {
    const copy = [...updated];
    copy[index].offerPrice = value;
    setUpdated(copy);
  };

  const handlePublish = async (leg) => {
    await fetch(`/api/v1/admin/empty-legs/${leg._id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ offerPrice: leg.offerPrice, status: "published" }),
    });

    alert("Empty Leg Published!");
  };

  return (
    <div
      className="modal fade show"
      style={{
        display: "block",
        background: "rgba(0,0,0,0.6)",
        zIndex: 99999,
      }}
    >
      <div className="modal-dialog modal-lg modal-dialog-centered">
        <div className="modal-content">
          {/* Header */}
          <div className="modal-header">
            <h5 className="modal-title">Generated Empty Legs</h5>
            <button className="btn-close" onClick={onClose}></button>
          </div>

          {/* Body */}
          <div className="modal-body">
            {updated.map((leg, index) => (
              <div key={leg._id} className="border rounded p-3 mb-3">
                <h5 className="mb-2">
                  {leg.from} → {leg.to}
                </h5>

                <p className="mb-1">
                  <strong>Date:</strong> {leg.date} | <strong>Time:</strong>{" "}
                  {leg.time}
                </p>

                <p className="mb-2">
                  <strong>Exact Price:</strong> ₹{leg.exactPrice}
                </p>

                <label className="form-label">Offer Price</label>
                <input
                  type="number"
                  className="form-control"
                  value={leg.offerPrice || ""}
                  onChange={(e) => handlePriceChange(index, e.target.value)}
                />

                <button
                  className="btn btn-success mt-3"
                  onClick={() => handlePublish(leg)}
                >
                  Publish
                </button>
              </div>
            ))}
          </div>

          {/* Footer */}
          <div className="modal-footer">
            <button className="btn btn-secondary" onClick={onClose}>
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
