import React, { useEffect, useState } from "react";
import {
  useCreateFlightTypeMutation,
  useUpdateFlightTypeMutation,
} from "../../redux/api/flightTypeApi";
import toast from "react-hot-toast";
import MyEditorComponent from "../MyEditorComponent";
import { useGetFlightCategoriesQuery } from "../../redux/api/flightCategoryApi";
import { useGetPriceSettingsQuery } from "../../redux/api/priceSettingApi";
import Select from "react-select";
import { airports } from "airports-json";

const FlightTypeForm = ({ editingFlight, setShowForm, refetch }) => {
  const [form, setForm] = useState({
    name: "",
    capacity: "",
    description: "",
    maxSpeed: "",
    status: true,
    baggage: "",
    baseLocation: "",
    pilots: "",
    flightAttendant: false,
    flightCategory: "",
  });

  const [existingImages, setExistingImages] = useState([]);
  const [newImages, setNewImages] = useState([]);
  const [previewImages, setPreviewImages] = useState([]);
  const [removedImageIds, setRemovedImageIds] = useState([]);
  const [priceHeads, setPriceHeads] = useState([{ head: "", amount: "" }]);

  const [createFlightType] = useCreateFlightTypeMutation();
  const [updateFlightType] = useUpdateFlightTypeMutation();

  const { data: categoryData, isLoading: categoriesLoading } =
    useGetFlightCategoriesQuery();
  const { data: priceHeadData, isLoading: priceHeadLoading } =
    useGetPriceSettingsQuery();

  // Prefill form in edit mode
  useEffect(() => {
    if (editingFlight) {
      setForm({
        name: editingFlight.name || "",
        capacity: editingFlight.capacity || "",
        description: editingFlight.description || "",
        maxSpeed: editingFlight.maxSpeed || "",
        status: editingFlight.status ?? true,
        baggage: editingFlight.baggage || "",
        baseLocation: editingFlight.baseLocation || "",
        pilots: editingFlight.pilots || "",
        flightAttendant: editingFlight.flightAttendant || false,
        flightCategory: editingFlight.flightCategory?._id || "",
      });

      setExistingImages(editingFlight.images || []);

      // Pre-fill Price Heads correctly
      if (editingFlight.priceHeads && editingFlight.priceHeads.length > 0) {
        const formattedHeads = editingFlight.priceHeads.map((ph) => ({
          head: ph.head || "", // must match select value
          amount: ph.amount || "",
        }));
        setPriceHeads(formattedHeads);
      } else {
        setPriceHeads([{ head: "", amount: "" }]);
      }
    }
  }, [editingFlight]);

  // Manage image previews
  useEffect(() => {
    const previews = [
      ...existingImages.map((img) => ({ type: "existing", src: img.url })),
      ...newImages.map((item) => ({ type: "new", src: item.preview })),
    ];
    setPreviewImages(previews);
  }, [existingImages, newImages]);

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === "file") {
      const selectedFiles = Array.from(files).map((file) => ({
        file,
        preview: URL.createObjectURL(file),
      }));
      setNewImages((prev) => [...prev, ...selectedFiles]);
    } else {
      setForm((prev) => ({
        ...prev,
        [name]: type === "checkbox" ? checked : value,
      }));
    }
  };

  // Remove image
  const removeImage = (index) => {
    const imgToRemove = previewImages[index];
    if (imgToRemove.type === "existing") {
      const removedImage = existingImages.find(
        (img) => img.url === imgToRemove.src
      );
      if (removedImage) {
        setRemovedImageIds((prev) => [...prev, removedImage.public_id]);
        setExistingImages((prev) =>
          prev.filter((img) => img.url !== imgToRemove.src)
        );
      }
    } else {
      setNewImages((prev) =>
        prev.filter((img) => img.preview !== imgToRemove.src)
      );
    }
  };

  // Price Heads handlers
  const handlePriceHeadChange = (index, field, value) => {
    setPriceHeads((prev) =>
      prev.map((item, i) => (i === index ? { ...item, [field]: value } : item))
    );
  };
  const addPriceHeadRow = () =>
    setPriceHeads((prev) => [...prev, { head: "", amount: "" }]);
  const removePriceHeadRow = (index) =>
    setPriceHeads((prev) => prev.filter((_, i) => i !== index));

  // Submit handler
  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.entries(form).forEach(([key, value]) => formData.append(key, value));

    formData.append("priceHeads", JSON.stringify(priceHeads));

    existingImages.forEach((img) =>
      formData.append("existingImages[]", img.url)
    );
    removedImageIds.forEach((id) => formData.append("removedImages[]", id));
    newImages.forEach((imgObj) => formData.append("images", imgObj.file));

    try {
      if (editingFlight) {
        await updateFlightType({ id: editingFlight._id, formData }).unwrap();
        toast.success("Flight type updated successfully!");
      } else {
        await createFlightType(formData).unwrap();
        toast.success("Flight type created successfully!");
      }
      setShowForm(false);
      refetch();
    } catch (err) {
      console.error("Save Error:", err);
      toast.error("Failed to save flight type");
    }
  };

  // Airport options
  const airportOptions = airports
    .filter((a) => a.iata_code && a.name && a.municipality)
    .map((airport) => ({
      value: airport.iata_code,
      label: `${airport.name} (${airport.iata_code}) - ${airport.municipality}`,
    }));

  return (
    <div className="card p-3 mb-4">
      <form
        className="border p-4 mb-4"
        onSubmit={handleSubmit}
        encType="multipart/form-data"
      >
        <h5>{editingFlight ? "Edit" : "Add"} Flight Details</h5>
        <div className="row">
          {/* Flight Name */}
          <div className="col-md-4 mb-3">
            <input
              type="text"
              name="name"
              placeholder="Flight Name"
              className="form-control"
              value={form.name}
              onChange={handleChange}
              required
            />
          </div>

          {/* Flight Category */}
          <div className="col-md-4 mb-4">
            <select
              name="flightCategory"
              className="form-control"
              value={form.flightCategory}
              onChange={handleChange}
              required
            >
              <option value="">Select Flight Category</option>
              {!categoriesLoading &&
                categoryData?.flightCategories?.map((cat) => (
                  <option key={cat._id} value={cat._id}>
                    {cat.name}
                  </option>
                ))}
            </select>
          </div>

          {/* Base Airport */}
          <div className="col-md-4 mb-4">
            <Select
              options={airportOptions}
              value={airportOptions.find(
                (airport) => airport.value === form.baseLocation
              )}
              onChange={(selected) =>
                setForm((prev) => ({
                  ...prev,
                  baseLocation: selected?.value || "",
                }))
              }
              placeholder="Search Base Airport (IATA)"
              isClearable
              isSearchable
              required
            />
          </div>

          {/* Other Inputs */}
          <div className="col-md-4 mb-4">
            <input
              type="number"
              name="capacity"
              placeholder="Capacity (Pax)"
              className="form-control"
              value={form.capacity}
              onChange={handleChange}
              required
            />
          </div>

          <div className="col-md-4 mb-4">
            <input
              type="number"
              name="maxSpeed"
              placeholder="Max Speed (km/h)"
              className="form-control"
              value={form.maxSpeed}
              onChange={handleChange}
              required
            />
          </div>

          <div className="col-md-4 mb-4">
            <input
              type="number"
              name="baggage"
              placeholder="Baggage Allowance (kg)"
              className="form-control"
              value={form.baggage}
              onChange={handleChange}
              required
            />
          </div>

          <div className="col-md-4 mb-4">
            <input
              type="number"
              name="pilots"
              placeholder="Pilots"
              className="form-control"
              value={form.pilots}
              onChange={handleChange}
            />
          </div>

          <div className="col-md-4 mb-4 d-flex align-items-center">
            <label className="form-check-label me-2">Flight Attendant:</label>
            <input
              type="checkbox"
              name="flightAttendant"
              checked={form.flightAttendant}
              onChange={handleChange}
            />
          </div>

          {/* Description */}
          <div className="col-md-12 mb-4">
            <MyEditorComponent
              value={form.description}
              onChange={(value) =>
                setForm((prev) => ({ ...prev, description: value }))
              }
            />
          </div>

          {/* Image Upload */}
          <div className="col-md-6 mb-4">
            <label className="form-label">Upload Flight Images</label>
            <input
              type="file"
              name="images"
              className="form-control"
              accept="image/*"
              onChange={handleChange}
              multiple
              required={!editingFlight}
            />
            <div className="d-flex flex-wrap mt-2">
              {previewImages.map((img, idx) => (
                <div
                  key={idx}
                  className="position-relative me-2 mb-2"
                  style={{ width: "100px" }}
                >
                  <img
                    src={img.src}
                    alt={`Preview ${idx}`}
                    className="img-thumbnail"
                  />
                  <button
                    type="button"
                    className="btn btn-sm btn-danger position-absolute top-0 end-0 translate-middle"
                    style={{ borderRadius: "50%" }}
                    onClick={() => removeImage(idx)}
                  >
                    <i className="bi bi-x"></i>
                  </button>
                </div>
              ))}
            </div>
          </div>

          {/* Status */}
          <div className="col-md-6 mb-4 d-flex align-items-center">
            <label className="form-check-label me-2">Active:</label>
            <input
              type="checkbox"
              name="status"
              checked={form.status}
              onChange={handleChange}
            />
          </div>

          {/* Price Heads */}
          <div className="col-md-12 mb-4">
            <h6 className="fw-bold mb-3">Pricing Heads</h6>
            {priceHeads.map((item, index) => (
              <div
                key={index}
                className="row align-items-center mb-2 border-bottom pb-2"
              >
                <div className="col-md-6 mb-2">
                  <select
                    className="form-control"
                    value={item.head}
                    onChange={(e) =>
                      handlePriceHeadChange(index, "head", e.target.value)
                    }
                  >
                    <option value="">Select Price Head</option>
                    {!priceHeadLoading &&
                      priceHeadData?.heads?.map((ph) => (
                        <option key={ph._id} value={ph._id}>
                          {ph.headName}
                        </option>
                      ))}
                  </select>
                </div>

                <div className="col-md-4 mb-2">
                  <input
                    type="number"
                    className="form-control"
                    placeholder="Amount"
                    value={item.amount}
                    onChange={(e) =>
                      handlePriceHeadChange(index, "amount", e.target.value)
                    }
                  />
                </div>

                <div className="col-md-2 mb-2 text-center">
                  {priceHeads.length > 1 && (
                    <button
                      type="button"
                      className="btn btn-danger btn-sm"
                      onClick={() => removePriceHeadRow(index)}
                    >
                      Delete
                    </button>
                  )}
                </div>
              </div>
            ))}
            <button
              type="button"
              className="btn btn-primary btn-sm mt-2"
              onClick={addPriceHeadRow}
            >
              + Add More
            </button>
          </div>
        </div>

        <button className="btn btn-success">
          {editingFlight ? "Update" : "Save"}
        </button>
        <button
          type="button"
          className="btn btn-secondary ms-2"
          onClick={() => setShowForm(false)}
        >
          Cancel
        </button>
      </form>
    </div>
  );
};

export default FlightTypeForm;
