import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { toast } from "react-hot-toast";
import axios from "axios";
import { Editor } from "@tinymce/tinymce-react";

const FlightCategoryForm = () => {
  const navigate = useNavigate();
  const { id } = useParams(); // if editing
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState("");
  const [uploading, setUploading] = useState(false);
  const [preview, setPreview] = useState("");

  // Fetch category if editing
  useEffect(() => {
    if (id) {
      axios.get(`/api/flight-categories/${id}`).then((res) => {
        const data = res.data;
        setName(data.name);
        setDescription(data.description);
        setImage(data.image);
        setPreview(data.image);
      });
    }
  }, [id]);

  // Image upload to Cloudinary
  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setUploading(true);

    const formData = new FormData();
    formData.append("file", file);
    formData.append("upload_preset", "YOUR_CLOUDINARY_PRESET");

    try {
      const res = await axios.post(
        "https://api.cloudinary.com/v1_1/YOUR_CLOUD_NAME/image/upload",
        formData
      );
      setImage(res.data.secure_url);
      setPreview(res.data.secure_url);
      toast.success("Image uploaded successfully");
    } catch (err) {
      toast.error("Image upload failed");
    } finally {
      setUploading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !description || !image) {
      toast.error("Please fill all fields");
      return;
    }

    const payload = { name, description, image };

    try {
      if (id) {
        await axios.put(`/api/flight-categories/${id}`, payload);
        toast.success("Category updated successfully");
      } else {
        await axios.post(`/api/flight-categories`, payload);
        toast.success("Category added successfully");
      }
      navigate("/flight-categories");
    } catch (err) {
      toast.error("Something went wrong");
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md max-w-3xl mx-auto">
      <h2 className="text-2xl font-semibold mb-4">
        {id ? "Edit Flight Category" : "Add Flight Category"}
      </h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Name */}
        <div>
          <label className="block font-medium mb-1">Name</label>
          <input
            type="text"
            className="w-full border p-2 rounded"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>

        {/* Description */}
        <div>
          <label className="block font-medium mb-1">Description</label>
          <Editor
            apiKey="YOUR_TINYMCE_API_KEY"
            value={description}
            init={{
              height: 200,
              menubar: false,
              plugins: [
                "advlist autolink lists link image charmap preview anchor",
                "searchreplace visualblocks code fullscreen",
                "insertdatetime media table paste code help wordcount",
              ],
              toolbar:
                "undo redo | formatselect | bold italic backcolor | \
                 alignleft aligncenter alignright alignjustify | \
                 bullist numlist outdent indent | removeformat | help",
            }}
            onEditorChange={(content) => setDescription(content)}
          />
        </div>

        {/* Image Upload */}
        <div>
          <label className="block font-medium mb-1">Image</label>
          <input type="file" accept="image/*" onChange={handleImageUpload} />
          {uploading && <p className="text-sm text-gray-500">Uploading...</p>}
          {preview && (
            <img
              src={preview}
              alt="Preview"
              className="w-32 h-32 object-cover mt-2 rounded"
            />
          )}
        </div>

        {/* Submit Button */}
        <div>
          <button
            type="submit"
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
            disabled={uploading}
          >
            {id ? "Update" : "Add"}
          </button>
        </div>
      </form>
    </div>
  );
};

export default FlightCategoryForm;
