import React, { useEffect, useState } from "react";
import {
  useCreateAirportMutation,
  useUpdateAirportMutation,
} from "../../redux/api/airportApi";
import toast from "react-hot-toast";
import { airports, regions, countries } from "airports-json";

const AirportForm = ({ editingAirport, setShowForm, refetch }) => {
  const [form, setForm] = useState({
    name: "",
    code: "",
    shortName: "",
    city: "",
    country: "",
    coordinates: [], // [lng, lat]
    status: "active",
  });

  //const [airports, setAirports] = useState([]);
  const [search, setSearch] = useState("");
  const [suggestions, setSuggestions] = useState([]);

  const [createAirport] = useCreateAirportMutation();
  const [updateAirport] = useUpdateAirportMutation();

  // Load dataset
  // useEffect(() => {
  //   fetch("/data/airports.json")
  //     .then((res) => res.json())
  //     .then((data) => setAirports(data))
  //     .catch((err) => console.error("Failed to load airports dataset", err));
  // }, []);

  useEffect(() => {
    if (editingAirport) {
      setForm({
        name: editingAirport.name || "",
        code: editingAirport.code || "",
        shortName: editingAirport.shortName || "",
        city: editingAirport.city || "",
        country: editingAirport.country || "",
        coordinates: editingAirport.location?.coordinates || [],
        status: editingAirport.status || "active",
      });
    }
  }, [editingAirport]);

  // Search filtering
  useEffect(() => {
    if (search.length > 1) {
      const filtered = airports.filter(
        (a) =>
          a.name.toLowerCase().includes(search.toLowerCase()) ||
          a.city.toLowerCase().includes(search.toLowerCase()) ||
          a.iata.toLowerCase().includes(search.toLowerCase())
      );
      setSuggestions(filtered.slice(0, 10)); // top 10
    } else {
      setSuggestions([]);
    }
  }, [search, airports]);

  const handleSelectAirport = (airport) => {
    setForm({
      ...form,
      name: airport.name,
      code: airport.iata,
      shortName: airport.iata,
      city: airport.city,
      country: airport.country,
      coordinates: [airport.lng, airport.lat],
    });
    setSearch(`${airport.name} (${airport.iata})`);
    setSuggestions([]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.coordinates?.length) {
      toast.error("Coordinates are required");
      return;
    }

    try {
      if (editingAirport) {
        await updateAirport({ id: editingAirport._id, body: form }).unwrap();
        toast.success("Airport updated successfully");
      } else {
        await createAirport(form).unwrap();
        toast.success("Airport created successfully");
      }

      setShowForm(false);
      refetch();
    } catch (err) {
      toast.error("Failed to save airport");
      console.error(err);
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="border p-3 mb-3 bg-light"
      encType="multipart/form-data"
    >
      <div className="row g-3">
        {/* Airport Search Field */}
        <div className="col-md-6 position-relative">
          <input
            type="text"
            className="form-control"
            placeholder="Search Airport (name, city, or code)"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          {suggestions.length > 0 && (
            <ul
              className="list-group position-absolute w-100"
              style={{ zIndex: 1000 }}
            >
              {suggestions.map((airport) => (
                <li
                  key={airport.iata}
                  className="list-group-item list-group-item-action"
                  onClick={() => handleSelectAirport(airport)}
                  style={{ cursor: "pointer" }}
                >
                  {airport.name} ({airport.iata}) â€“ {airport.city},{" "}
                  {airport.country}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Auto-filled fields */}
        {["name", "code", "shortName", "city", "country"].map((field) => (
          <div className="col-md-4" key={field}>
            <input
              className="form-control"
              name={field}
              placeholder={field.charAt(0).toUpperCase() + field.slice(1)}
              value={form[field]}
              onChange={(e) =>
                setForm({ ...form, [field]: e.target.value.trimStart() })
              }
              required
              readOnly // prevent manual change
            />
          </div>
        ))}

        <div className="col-md-4">
          <select
            className="form-select"
            value={form.status}
            onChange={(e) => setForm({ ...form, status: e.target.value })}
          >
            <option value="active">Active</option>
            <option value="inactive">Inactive</option>
          </select>
        </div>

        <div className="col-md-12 d-flex gap-2 mt-2">
          <button className="btn btn-success" type="submit">
            {editingAirport ? "Update" : "Add"} Airport
          </button>
          <button
            className="btn btn-secondary"
            type="button"
            onClick={() => setShowForm(false)}
          >
            Cancel
          </button>
        </div>
      </div>
    </form>
  );
};

export default AirportForm;
