import React from "react";
import { useGetBookingHistoryQuery } from "../../redux/api/bookingApi";
import DataTable from "react-data-table-component";

const BookingHistoryModal = ({ booking, onClose }) => {
  const { data, isLoading } = useGetBookingHistoryQuery(booking._id);
  const histories = data?.histories || [];

  const columns = [
    {
      name: "Change Type",
      selector: (row) => row.changeType,
      sortable: true,
      center: true,
    },
    {
      name: "Updated By",
      selector: (row) => row.updatedBy?.name || "-",
      center: true,
    },
    {
      name: "Date",
      selector: (row) =>
        new Date(row.createdAt).toLocaleString("en-IN", {
          dateStyle: "medium",
          timeStyle: "short",
        }),
      center: true,
    },
    {
      name: "Old Data (JSON)",
      cell: (row) => (
        <div className="table-responsive">
          <table className="table table-bordered table-striped">
            <thead>
              <tr>
                <th>Date</th>
                <th>Changed By</th>
                <th>Old Data</th>
              </tr>
            </thead>
            <tbody>
              {booking.history?.length ? (
                booking.history.map((item, index) => (
                  <tr key={index}>
                    <td>{new Date(item.changedAt).toLocaleString()}</td>
                    <td>{item.changedBy?.name || "System"}</td>
                    <td>
                      <ul className="mb-0">
                        {Object.entries(item.oldData).map(([key, value]) => (
                          <li key={key}>
                            <strong>{key.replace(/([A-Z])/g, " $1")}:</strong>{" "}
                            {typeof value === "object"
                              ? JSON.stringify(value)
                              : String(value)}
                          </li>
                        ))}
                      </ul>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="3" className="text-center">
                    No history available.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      ),
    },
  ];

  return (
    <div
      className="modal fade show"
      style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}
    >
      <div className="modal-dialog modal-xl">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">
              Booking History â€” {booking.user?.name}
            </h5>
            <button
              type="button"
              className="btn-close"
              onClick={onClose}
            ></button>
          </div>
          <div className="modal-body">
            <DataTable
              columns={columns}
              data={histories}
              progressPending={isLoading}
              pagination
              highlightOnHover
              striped
              noDataComponent="No history found"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookingHistoryModal;
