import React, { useEffect, useState } from "react";
import toast from "react-hot-toast";
import {
  useGetRolesQuery,
  useUpdateUserRoleMutation,
} from "../../redux/api/adminApi";

const UserRow = ({ user }) => {
  const [role, setRole] = useState("");
  const [updateUserRole, { isLoading }] = useUpdateUserRoleMutation();

  const { data: roleData, isLoading: rolesLoading } = useGetRolesQuery();
  console.log(roleData);

  // Set user's current role when roles are loaded
  useEffect(() => {
    if (roleData?.length && user?.role) {
      console.log(user.role);
      setRole(user.role); // Ensure role matches one from list
    }
  }, [roleData, user]);

  const handleChange = async () => {
    try {
      await updateUserRole({ id: user._id, role }).unwrap();
      toast.success("Role updated successfully");
    } catch (err) {
      toast.error(err?.data?.message || "Error updating role");
    }
  };

  return (
    <tr>
      <td>{user.name}</td>
      <td>{user.email}</td>
      <td>{user.role}</td>
      <td>
        {rolesLoading ? (
          <span>Loading roles...</span>
        ) : (
          <>
            <select
              className="form-select"
              value={role}
              onChange={(e) => setRole(e.target.value)}
              disabled={isLoading}
            >
              {roleData?.map((r) => (
                <option key={r._id} value={r.name}>
                  {r.name.charAt(0).toUpperCase() + r.name.slice(1)}
                </option>
              ))}
            </select>
            <button
              className="btn btn-sm btn-primary mt-1"
              onClick={handleChange}
              disabled={isLoading || role === user.role}
            >
              Update
            </button>
          </>
        )}
      </td>
    </tr>
  );
};
export default UserRow;
