import { createSlice } from "@reduxjs/toolkit";
import { authApi } from "../api/authApi"; // your RTK Query api

const authSlice = createSlice({
    name: "auth",
    initialState: {
        user: null,
        token: null,
        isAuthenticated: false,
    },
    reducers: {
        logout: (state) => {
            state.user = null;
            state.token = null;
            state.isAuthenticated = false;
            localStorage.removeItem("authToken"); // optional
        },
    },
    extraReducers: (builder) => {
        builder
            // This is CRITICAL – runs when login succeeds
            .addMatcher(
                authApi.endpoints.login.matchFulfilled,
                (state, { payload }) => {
                    state.token = payload.token;
                    state.user = payload.user; // { id, role, name }
                    state.isAuthenticated = true; // ← THIS MUST BE TRUE
                }
            )
            .addMatcher(
                authApi.endpoints.login.matchRejected,
                (state) => {
                    state.isAuthenticated = false;
                }
            );
    },
});

export const { logout } = authSlice.actions;
export default authSlice.reducer;
