import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import {
  useGetModelProfileQuery,
  useUpdateModelProfileMutation,
} from "../../redux/api/modelApi";
import toast from "react-hot-toast";

const ModelProfile = () => {
  const { user } = useSelector((state) => state.auth);
  const { data, isLoading } = useGetModelProfileQuery(user.id);
  const [updateProfile, { isLoading: isUpdating }] =
    useUpdateModelProfileMutation();

  const [formData, setFormData] = useState({
    stageName: "",
    tagline: "",
    age: "",
    based_in: "",
    nationality: "",
    // Add all other fields from Model schema
  });

  useEffect(() => {
    if (data) {
      setFormData(data.model);
    }
  }, [data]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateProfile(formData).unwrap();
      toast.success("Profile updated successfully");
    } catch (err) {
      toast.error("Failed to update profile");
    }
  };

  if (isLoading) return <div>Loading...</div>;

  return (
    <div className="page-inner">
      <div className="page-header">
        <h3 className="fw-bold mb-3">My Profile</h3>
      </div>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Stage Name</label>
          <input
            type="text"
            name="stageName"
            value={formData.stageName}
            onChange={handleChange}
            className="form-control"
            required
          />
        </div>
        <div className="mb-3">
          <label>Tagline</label>
          <input
            type="text"
            name="tagline"
            value={formData.tagline}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label>Age</label>
          <input
            type="number"
            name="age"
            value={formData.age}
            onChange={handleChange}
            className="form-control"
            min="18"
          />
        </div>
        {/* Add more fields */}
        <button type="submit" className="btn btn-primary" disabled={isUpdating}>
          {isUpdating ? "Updating..." : "Update Profile"}
        </button>
      </form>
    </div>
  );
};

export default ModelProfile;
