import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";

export const masterApi = createApi({
    reducerPath: "masterApi",
    baseQuery: fetchBaseQuery({
        baseUrl: "/api/v1",
        credentials: "include",
    }),
    tagTypes: ["Master"],
    endpoints: (builder) => ({
        getMasters: builder.query({
            query: (type) => `/admin/masters?type=${type}`,
            providesTags: (result, error, type) =>
                result?.masters
                    ? [
                        ...result.masters.map(({ _id }) => ({
                            type: "Master",
                            id: _id,
                        })),
                        { type: "Master", id: "LIST" },
                    ]
                    : [{ type: "Master", id: "LIST" }],
        }),

        createMaster: builder.mutation({
            query: (body) => ({
                url: "/admin/masters",
                method: "POST",
                body,
            }),
            invalidatesTags: [{ type: "Master", id: "LIST" }],
        }),

        updateMaster: builder.mutation({
            query: ({ id, ...data }) => ({
                url: `/admin/masters/${id}`,
                method: "PUT",
                body: data,
            }),
            invalidatesTags: (_r, _e, { id }) => [
                { type: "Master", id },
                { type: "Master", id: "LIST" },
            ],
        }),

        deleteMaster: builder.mutation({
            query: (id) => ({
                url: `/admin/masters/${id}`,
                method: "DELETE",
            }),
            invalidatesTags: [{ type: "Master", id: "LIST" }],
        }),
    }),
});

export const {
    useGetMastersQuery,
    useCreateMasterMutation,
    useUpdateMasterMutation,
    useDeleteMasterMutation,
} = masterApi;
