// components/models/AddModelForm.jsx
import React, { useEffect, useRef, useState } from "react";
import {
  useCreateModelMutation,
  useUpdateModelMutation,
  useUploadProfileMutation,
  useUploadPortfolioMutation,
  useSaveProfileMutation,
  useSavePortfolioMutation,
  useDeletePortfolioImageMutation,
} from "../../redux/api/modelApi";
import toast from "react-hot-toast";
import { useLazyGetUploadSignatureQuery } from "../../redux/api/cloudinaryApi";

const AddModelForm = ({ model, onSaved }) => {
  const ref = useRef(null);
  const [form, setForm] = useState({
    name: "",
    gender: "Female",
    email: "",
    phone: "",
    dob: "",
    city: "",
    height: "",
    weight: "",
    skills: "",
    experienceLevel: "Beginner",
    status: "pending",
    featured: false,
  });

  const [profileFile, setProfileFile] = useState(null);
  const [portfolioFiles, setPortfolioFiles] = useState([]);

  const [localPortfolio, setLocalPortfolio] = useState([]);

  const [createModel, { isLoading: creating }] = useCreateModelMutation();
  const [updateModel, { isLoading: updating }] = useUpdateModelMutation();
  const [uploadProfile] = useUploadProfileMutation();
  const [uploadPortfolio] = useUploadPortfolioMutation();
  const [saveProfile] = useSaveProfileMutation();
  const [savePortfolio] = useSavePortfolioMutation();
  const [deletePortfolioImage] = useDeletePortfolioImageMutation();
  const [getSignature] = useLazyGetUploadSignatureQuery();

  useEffect(() => {
    if (model?.portfolioImages) {
      setLocalPortfolio(localPortfolio);
    }
  }, [model]);

  useEffect(() => {
    if (model) {
      setForm({
        name: model.name || "",
        gender: model.gender || "Female",
        email: model.email || "",
        phone: model.phone || "",
        dob: model.dob ? model.dob.slice(0, 10) : "",
        city: model.city || "",
        height: model.height || "",
        weight: model.weight || "",
        skills: model.skills ? model.skills.join(",") : "",
        experienceLevel: model.experienceLevel || "Beginner",
        status: model.status || "pending",
        featured: model.featured || false,
      });

      setTimeout(() => ref.current?.scrollIntoView({ behavior: "smooth" }), 80);
    }
  }, [model]);

  const toBase64 = (file) =>
    new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (err) => reject(err);
    });

  const handleProfileChange = (e) => setProfileFile(e.target.files[0]);
  const handlePortfolioChange = (e) =>
    setPortfolioFiles(Array.from(e.target.files));

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = {
        ...form,
        skills: form.skills ? form.skills.split(",").map((s) => s.trim()) : [],
      };

      let res;

      // UPDATE
      if (model?._id) {
        res = await updateModel({ id: model._id, data: payload }).unwrap();
      }
      // CREATE
      else {
        res = await createModel(payload).unwrap();
      }

      // Extract model ID correctly
      const modelId = model?._id || res.model?._id || res._id;

      // Upload profile image
      if (profileFile) {
        const uploaded = await uploadToCloudinary(
          profileFile,
          "Aayo/models/profile"
        );

        await saveProfile({
          id: modelId,
          url: uploaded.secure_url,
          public_id: uploaded.public_id,
        }).unwrap();
      }

      // Upload portfolio images
      if (portfolioFiles.length > 0) {
        const uploads = [];

        for (const file of portfolioFiles) {
          const result = await uploadToCloudinary(
            file,
            "Aayo/models/portfolio"
          );
          uploads.push({
            url: result.secure_url,
            public_id: result.public_id,
          });
        }

        await savePortfolio({
          id: modelId,
          images: uploads,
        }).unwrap();
      }

      toast.success(`Model ${model ? "updated" : "created"} successfully`);
      if (onSaved) onSaved();
    } catch (err) {
      console.error(err);
      toast.error(err?.data?.message || "Save failed");
    }
  };

  const isLoading = creating || updating;

  const uploadToCloudinary = async (file, folder) => {
    const { data: sig } = await getSignature(folder).unwrap();

    const formData = new FormData();
    formData.append("file", file);
    formData.append("api_key", sig.apiKey);
    formData.append("timestamp", sig.timestamp);
    formData.append("signature", sig.signature);
    formData.append("folder", folder);

    const cloudRes = await fetch(
      `https://api.cloudinary.com/v1_1/${sig.cloudName}/auto/upload`,
      { method: "POST", body: formData }
    );

    return await cloudRes.json();
  };

  const handleDeletePortfolio = async (public_id) => {
    if (!window.confirm("Delete this image?")) return;

    try {
      await deletePortfolioImage({
        id: model._id,
        public_id,
      }).unwrap();

      // Optimistically update UI
      setLocalPortfolio((prev) =>
        prev.filter((p) => p.public_id !== public_id)
      );

      toast.success("Image removed");
    } catch (err) {
      toast.error("Failed to delete");
    }
  };

  return (
    <form
      ref={ref}
      onSubmit={handleSubmit}
      className="border p-3 mb-4 bg-light"
    >
      <div className="row g-3">
        <div className="col-md-4">
          <label>Name</label>
          <input
            required
            value={form.name}
            onChange={(e) => setForm((p) => ({ ...p, name: e.target.value }))}
            className="form-control"
          />
        </div>

        <div className="col-md-4">
          <label>Gender</label>
          <select
            className="form-control"
            value={form.gender}
            onChange={(e) => setForm((p) => ({ ...p, gender: e.target.value }))}
          >
            <option>Female</option>
            <option>Male</option>
            <option>Other</option>
          </select>
        </div>

        <div className="col-md-4">
          <label>City</label>
          <input
            value={form.city}
            onChange={(e) => setForm((p) => ({ ...p, city: e.target.value }))}
            className="form-control"
          />
        </div>

        <div className="col-md-4">
          <label>Email</label>
          <input
            value={form.email}
            onChange={(e) => setForm((p) => ({ ...p, email: e.target.value }))}
            className="form-control"
          />
        </div>

        <div className="col-md-4">
          <label>Phone</label>
          <input
            value={form.phone}
            onChange={(e) => setForm((p) => ({ ...p, phone: e.target.value }))}
            className="form-control"
          />
        </div>

        <div className="col-md-4">
          <label>Date of Birth</label>
          <input
            type="date"
            value={form.dob}
            onChange={(e) => setForm((p) => ({ ...p, dob: e.target.value }))}
            className="form-control"
          />
        </div>

        <div className="col-md-3">
          <label>Height (cm)</label>
          <input
            type="number"
            value={form.height}
            onChange={(e) => setForm((p) => ({ ...p, height: e.target.value }))}
            className="form-control"
          />
        </div>

        <div className="col-md-3">
          <label>Weight (kg)</label>
          <input
            type="number"
            value={form.weight}
            onChange={(e) => setForm((p) => ({ ...p, weight: e.target.value }))}
            className="form-control"
          />
        </div>

        <div className="col-md-6">
          <label>Skills (comma separated)</label>
          <input
            value={form.skills}
            onChange={(e) => setForm((p) => ({ ...p, skills: e.target.value }))}
            className="form-control"
          />
        </div>

        <div className="col-md-4">
          <label>Experience Level</label>
          <select
            className="form-control"
            value={form.experienceLevel}
            onChange={(e) =>
              setForm((p) => ({ ...p, experienceLevel: e.target.value }))
            }
          >
            <option>Beginner</option>
            <option>Intermediate</option>
            <option>Professional</option>
          </select>
        </div>

        <div className="col-md-4">
          <label>Profile Image</label>
          <input
            type="file"
            accept="image/*"
            onChange={handleProfileChange}
            className="form-control"
          />
        </div>

        {/* Existing portfolio images for edit mode */}
        {model?._id && localPortfolio?.length > 0 && (
          <div className="col-md-12">
            <label className="fw-bold">Existing Portfolio Images</label>

            <div className="d-flex flex-wrap gap-3 mt-2">
              {localPortfolio.map((img) => (
                <div
                  key={img.public_id}
                  style={{
                    position: "relative",
                    width: 120,
                    height: 120,
                    borderRadius: 6,
                    overflow: "hidden",
                    border: "1px solid #ddd",
                  }}
                >
                  <img
                    src={img.url}
                    alt=""
                    style={{
                      width: "100%",
                      height: "100%",
                      objectFit: "cover",
                    }}
                  />

                  <button
                    type="button"
                    className="btn btn-danger btn-sm"
                    style={{
                      position: "absolute",
                      top: 5,
                      right: 5,
                      padding: "0px 6px",
                      lineHeight: "14px",
                      fontSize: "12px",
                    }}
                    onClick={() => handleDeletePortfolio(img.public_id)}
                  >
                    âœ•
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}

        <div className="col-md-8">
          <label>Portfolio Images (multiple)</label>
          <input
            type="file"
            accept="image/*,video/*"
            multiple
            onChange={handlePortfolioChange}
            className="form-control"
          />
        </div>

        <div className="col-md-12 d-flex gap-2 mt-3">
          <button
            type="submit"
            className="btn btn-success"
            disabled={isLoading}
          >
            {isLoading
              ? "Processing..."
              : model
              ? "Update Model"
              : "Create Model"}
          </button>

          <button
            type="button"
            className="btn btn-secondary"
            onClick={() => {
              if (onSaved) onSaved();
            }}
          >
            Cancel
          </button>
        </div>
      </div>
    </form>
  );
};

export default AddModelForm;
