// src/redux/slices/authSlice.js

import { createSlice } from '@reduxjs/toolkit';
import { authApi } from '../api/authApi'; // Adjust path if needed

const authSlice = createSlice({
    name: 'auth',
    initialState: {
        user: null,
        token: null,
        isAuthenticated: false,
    },
    reducers: {
        logout: (state) => {
            state.user = null;
            state.token = null;
            state.isAuthenticated = false;
            localStorage.removeItem('authToken'); // or whatever key you use
        },
    },
    extraReducers: (builder) => {
        builder
            // THIS IS THE MISSING PART!
            .addMatcher(
                authApi.endpoints.login.matchFulfilled,
                (state, { payload }) => {
                    console.log('Login successful, updating Redux state:', payload);
                    state.token = payload.token;
                    state.user = payload.user; // { id, role, name }
                    state.isAuthenticated = true;

                    // Persist token so it survives page refresh
                    localStorage.setItem('authToken', payload.token);
                }
            )
            .addMatcher(
                authApi.endpoints.login.matchRejected,
                (state) => {
                    state.isAuthenticated = false;
                    state.user = null;
                    state.token = null;
                }
            )
            // Optional: Keep user in sync when getProfile succeeds (e.g., on refresh)
            .addMatcher(
                authApi.endpoints.getProfile?.matchFulfilled,
                (state, { payload }) => {
                    state.user = payload.user;
                    state.isAuthenticated = true;
                }
            );
    },
});

export const { logout } = authSlice.actions;
export default authSlice.reducer;
