import React, { useState, useEffect } from "react";
import {
  useGetEmptyLegsQuery,
  useDeleteEmptyLegMutation,
  useToggleEmptyLegStatusMutation,
} from "../../redux/api/emptyLegApi";

import EmptyLegForm from "./EmptyLegForm";
import DataTable from "react-data-table-component";
import toast from "react-hot-toast";
import { useGetAirportsQuery } from "../../redux/api/airportApi";

const EmptyLegList = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredLegs, setFilteredLegs] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingLeg, setEditingLeg] = useState(null);

  const [statusFilter, setStatusFilter] = useState("all");

  const { data, isLoading } = useGetEmptyLegsQuery();
  const [deleteLeg] = useDeleteEmptyLegMutation();
  const [toggleStatus] = useToggleEmptyLegStatusMutation();

  const { data: airportData } = useGetAirportsQuery();
  const airports = airportData?.airports || [];

  const getAirportName = (iata) => {
    const ap = airports.find((a) => a.iata_code === iata);
    return ap ? ap.name : iata;
  };

  useEffect(() => {
    if (!data?.emptyLegs) return;

    const s = searchTerm.toLowerCase();

    const filtered = data.emptyLegs.filter((item) => {
      /* -----------------------------------------
       1. STATUS FILTER
    ------------------------------------------ */
      if (statusFilter !== "all" && item.status !== statusFilter) {
        return false;
      }

      /* -----------------------------------------
       2. MULTI-FIELD SMART SEARCH
    ------------------------------------------ */
      const fromName = getAirportName(item.from);
      const toName = getAirportName(item.to);

      const fields = [
        item.from,
        item.to,
        fromName,
        toName,
        item.flightType?.name,
        item.date,
        item.time,
        item.exactPrice?.toString(),
        item.offerPrice?.toString(),
      ];

      return fields.some((f) => f?.toLowerCase().includes(s));
    });

    setFilteredLegs(filtered);
  }, [data, searchTerm, statusFilter, airports]);

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this empty leg?")) return;
    try {
      await deleteLeg(id).unwrap();
      toast.success("Deleted successfully");
    } catch {
      toast.error("Delete failed");
    }
  };

  const handleToggle = async (id) => {
    await toggleStatus(id);
    toast.success("Status updated");
  };

  const columns = [
    {
      name: "Flight Type",
      selector: (row) => row.flightType?.name || "",
      sortable: true,
    },
    {
      name: "From - To",
      selector: (row) => `${row.from} → ${row.to}`,
      sortable: true,
    },
    {
      name: "Date",
      selector: (row) => row.date,
      sortable: true,
    },
    {
      name: "Time",
      selector: (row) => row.time,
      sortable: true,
    },
    {
      name: "Exact Price",
      selector: (row) => `₹${row.exactPrice}`,
      sortable: true,
    },
    {
      name: "Offer Price",
      selector: (row) => (row.offerPrice ? `₹${row.offerPrice}` : "—"),
      sortable: true,
    },
    {
      name: "Status",
      selector: (row) => {
        const status = row.status.charAt(0).toUpperCase() + row.status.slice(1);
        let badgeClass = "bg-secondary";

        if (row.status === "draft") {
          badgeClass = "bg-danger";
        } else if (row.status === "published") {
          badgeClass = "bg-warning";
        } else if (row.status === "booked") {
          badgeClass = "bg-success";
        }
        return <span className={`badge ${badgeClass}`}>{status}</span>;
      },
    },
    {
      name: "Actions",
      cell: (row) => (
        <div className="dropdown">
          <button
            className="btn btn-sm btn-secondary dropdown-toggle"
            type="button"
            data-bs-toggle="dropdown"
            aria-expanded="false"
          >
            Actions
          </button>
          <ul className="dropdown-menu">
            <li>
              <button
                className="dropdown-item"
                onClick={() => {
                  setEditingLeg(row);
                  setShowForm(true);
                }}
              >
                Edit
              </button>
            </li>
            <li>
              <button
                className="dropdown-item"
                onClick={() => handleDelete(row._id)}
              >
                Delete
              </button>
            </li>
            <li>
              <button
                className="dropdown-item"
                onClick={() => handleToggle(row._id)}
              >
                Toggle Status
              </button>
            </li>
          </ul>
        </div>
      ),
      ignoreRowClick: true,
      allowoverflow: true,
      button: true,
      center: true,
    },
  ];

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between mb-3">
        <h4>Empty Legs</h4>
        <button
          className="btn btn-primary"
          onClick={() => {
            setEditingLeg(null);
            setShowForm(!showForm);
          }}
        >
          {showForm ? "Close Form" : "Add Empty Leg"}
        </button>
      </div>

      {showForm && (
        <EmptyLegForm
          editingLeg={editingLeg}
          onSuccess={() => setShowForm(false)}
        />
      )}

      <input
        type="text"
        className="form-control mb-3"
        placeholder="Search empty legs..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <div className="mb-3 d-flex gap-2">
        {["all", "draft", "published", "booked"].map((st) => (
          <button
            key={st}
            className={`btn btn-sm ${
              statusFilter === st ? "btn-primary" : "btn-outline-primary"
            }`}
            onClick={() => setStatusFilter(st)}
          >
            {st === "all" ? "All" : st.charAt(0).toUpperCase() + st.slice(1)}
          </button>
        ))}
      </div>

      <DataTable
        columns={columns}
        data={filteredLegs}
        progressPending={isLoading}
        pagination
        highlightOnHover
      />
    </div>
  );
};

export default EmptyLegList;
