import React, { useEffect, useState } from "react";
import {
  useCreateAirportMutation,
  useUpdateAirportMutation,
} from "../../redux/api/airportApi";
import toast from "react-hot-toast";
import { airports, countries } from "airports-json";

const AirportForm = ({ editingAirport, setShowForm, refetch }) => {
  const [form, setForm] = useState({
    id: "",
    ident: "",
    type: "",
    name: "",
    latitude_deg: "",
    longitude_deg: "",
    elevation_ft: "",
    continent: "",
    iso_country: "",
    iso_region: "",
    municipality: "",
    scheduled_service: "",
    gps_code: "",
    iata_code: "",
    local_code: "",
    home_link: "",
    keywords: "",
    status: "active",
  });

  const [search, setSearch] = useState("");
  const [suggestions, setSuggestions] = useState([]);

  const [createAirport] = useCreateAirportMutation();
  const [updateAirport] = useUpdateAirportMutation();

  // Prefill for editing
  useEffect(() => {
    if (editingAirport) {
      setForm({
        ...editingAirport,
        elevation_ft: editingAirport.elevation_ft || "",
        keywords: editingAirport.keywords || "",
        home_link: editingAirport.home_link || "",
      });
      setSearch(
        `${editingAirport.name} (${
          editingAirport.iata_code || editingAirport.ident
        })`
      );
    }
  }, [editingAirport]);

  // Search Airport Suggestions (from airports-json)
  useEffect(() => {
    if (search.length > 1) {
      const filtered = airports.filter(
        (a) =>
          a.name.toLowerCase().includes(search.toLowerCase()) ||
          a.municipality?.toLowerCase().includes(search.toLowerCase()) ||
          a.iata_code?.toLowerCase().includes(search.toLowerCase())
      );
      setSuggestions(filtered.slice(0, 10));
    } else {
      setSuggestions([]);
    }
  }, [search]);

  // Select from suggestions â†’ auto-fill form
  const handleSelectAirport = (airport) => {
    setForm({
      id: airport.id,
      ident: airport.ident,
      type: airport.type,
      name: airport.name,
      latitude_deg: Number(airport.latitude_deg),
      longitude_deg: Number(airport.longitude_deg),
      elevation_ft: airport.elevation_ft ? Number(airport.elevation_ft) : "",
      continent: airport.continent,
      iso_country: airport.iso_country,
      iso_region: airport.iso_region,
      municipality: airport.municipality,
      scheduled_service: airport.scheduled_service,
      gps_code: airport.gps_code,
      iata_code: airport.iata_code,
      local_code: airport.local_code,
      home_link: airport.home_link,
      keywords: airport.keywords,
      status: "active",
    });

    setSearch(`${airport.name} (${airport.iata_code || airport.ident})`);
    setSuggestions([]);
  };

  // ðŸ”¹ Handle manual field change (for when JSON doesnâ€™t have it)
  const numericFields = ["latitude_deg", "longitude_deg", "elevation_ft"];

  const handleFieldChange = (e) => {
    const { name, value } = e.target;

    let newValue = value;

    // Auto-uppercase some codes
    if (
      name === "iata_code" ||
      name === "iso_country" ||
      name === "iso_region" ||
      name === "ident"
    ) {
      newValue = value.toUpperCase();
    }

    // Handle numeric fields
    if (numericFields.includes(name)) {
      newValue = value === "" ? "" : Number(value);
    }

    setForm((prev) => ({
      ...prev,
      [name]: newValue,
    }));
  };

  // Submit Handler
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.name || !form.iata_code) {
      toast.error("Airport Name and IATA Code are required.");
      return;
    }

    try {
      if (editingAirport) {
        await updateAirport({ id: editingAirport._id, body: form }).unwrap();
        toast.success("Airport updated successfully");
      } else {
        await createAirport(form).unwrap();
        toast.success("Airport created successfully");
      }

      setShowForm(false);
      refetch();
    } catch (err) {
      toast.error("Failed to save airport");
      console.error(err);
    }
  };

  return (
    <form className="border p-3 mb-3 bg-light" onSubmit={handleSubmit}>
      <div className="row g-3">
        {/* Search Field (optional helper via airports-json) */}
        <div className="col-md-6 position-relative">
          <label className="form-label">Search Airport</label>
          <input
            type="text"
            className="form-control"
            placeholder="Search by name, city, IATA"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />

          {/* Suggestion Dropdown */}
          {suggestions.length > 0 && (
            <ul
              className="list-group position-absolute w-100"
              style={{ zIndex: 1000, maxHeight: "250px", overflowY: "auto" }}
            >
              {suggestions.map((airport) => (
                <li
                  key={airport.id}
                  className="list-group-item list-group-item-action"
                  onClick={() => handleSelectAirport(airport)}
                  style={{ cursor: "pointer" }}
                >
                  {airport.name} ({airport.iata_code}) â€“ {airport.municipality},{" "}
                  {airport.iso_country}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Individual fields with labels (now EDITABLE) */}
        {[
          "id",
          "ident",
          "type",
          "name",
          "iata_code",
          "municipality",
          "iso_country",
          "iso_region",
          "continent",
          "latitude_deg",
          "longitude_deg",
          "elevation_ft",
          "scheduled_service",
          "gps_code",
          "local_code",
          "home_link",
          "keywords",
        ].map((field) => (
          <div className="col-md-4" key={field}>
            <label className="form-label">
              {field.replace(/_/g, " ").toUpperCase()}
            </label>
            <input
              type={
                field === "latitude_deg" ||
                field === "longitude_deg" ||
                field === "elevation_ft"
                  ? "number"
                  : "text"
              }
              className="form-control"
              name={field}
              value={form[field] ?? ""}
              onChange={handleFieldChange}
            />
          </div>
        ))}

        {/* Status */}
        <div className="col-md-4">
          <label className="form-label">STATUS</label>
          <select
            className="form-select"
            value={form.status}
            onChange={(e) =>
              setForm((prev) => ({ ...prev, status: e.target.value }))
            }
          >
            <option value="active">Active</option>
            <option value="inactive">Inactive</option>
          </select>
        </div>

        {/* Buttons */}
        <div className="col-md-12 mt-3 d-flex gap-2">
          <button type="submit" className="btn btn-success">
            {editingAirport ? "Update" : "Add"} Airport
          </button>
          <button
            type="button"
            className="btn btn-secondary"
            onClick={() => setShowForm(false)}
          >
            Cancel
          </button>
        </div>
      </div>
    </form>
  );
};

export default AirportForm;
