import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "../../utils/axios"; // IMPORTANT: use your axios instance

export default function Masters() {
  const { type } = useParams(); // ✅ comes from URL
  const [items, setItems] = useState([]);
  const [label, setLabel] = useState("");

  const load = async () => {
    const res = await axios.get(`/admin/masters?type=${type}`);
    setItems(res.data.masters);
  };

  useEffect(() => {
    if (type) load();
  }, [type]);

  const add = async () => {
    if (!label.trim()) return;

    await axios.post("/admin/masters", {
      type, // ✅ now always defined
      label,
      value: label.toLowerCase().replace(/\s+/g, "_"),
    });

    setLabel("");
    load();
  };

  return (
    <div className="card p-4">
      <h4 className="mb-3 text-capitalize">{type} Master</h4>

      <div className="d-flex gap-2 mb-3">
        <input
          value={label}
          onChange={(e) => setLabel(e.target.value)}
          placeholder={`Add ${type}`}
          className="form-control"
        />
        <button onClick={add} className="btn btn-primary">
          Add
        </button>
      </div>

      <ul className="list-group">
        {items.map((i) => (
          <li
            key={i._id}
            className="list-group-item d-flex justify-content-between"
          >
            {i.label}
          </li>
        ))}
      </ul>
    </div>
  );
}
