import React, { useState, useMemo } from "react";
import { useGetUsersQuery } from "../../redux/api/adminApi";
import toast from "react-hot-toast";
import DataTable from "react-data-table-component";
import AddUserForm from "./AddUserForm";

const UserList = () => {
  const { data, isLoading, error, refetch } = useGetUsersQuery();
  const [showAddForm, setShowAddForm] = useState(false);
  const [editUser, setEditUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState(""); // State to store the search term

  if (error) return toast.error("Failed to load users");

  // Filter data based on search term
  const filteredData = useMemo(() => {
    return data?.users.filter(
      (user) =>
        user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.role?.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
  }, [data?.users, searchTerm]);

  const columns = [
    {
      name: "Name",
      selector: (row) => row.name,
      sortable: true,
    },
    {
      name: "Email",
      selector: (row) => row.email,
      sortable: true,
    },
    {
      name: "Role",
      selector: (row) => row.role?.name || "No Role", // Adjust for role display
      sortable: true,
    },
    {
      name: "Actions",
      cell: (row) => (
        <button
          className="btn btn-sm btn-warning"
          onClick={() => {
            setEditUser(row);
            setShowAddForm(true);
          }}
        >
          Edit
        </button>
      ),
    },
  ];

  return (
    <div className="container mt-4">
      <h3>User Role Management</h3>

      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Search Users..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)} // Update search term
        />
      </div>

      <button
        className="btn btn-primary mb-3"
        onClick={() => {
          setEditUser(null); // Reset form to Add mode
          setShowAddForm(!showAddForm);
        }}
      >
        {showAddForm ? "Close Form" : "Add New User"}
      </button>

      {showAddForm && (
        <AddUserForm
          onClose={() => setShowAddForm(false)}
          refetch={refetch}
          user={editUser}
        />
      )}

      <DataTable
        columns={columns}
        data={filteredData} // Use filtered data for the table
        progressPending={isLoading}
        pagination
        highlightOnHover
        striped
        persistTableHead
        noDataComponent="No users found"
      />
    </div>
  );
};

export default UserList;
