import { useSelector } from "react-redux";
import { useGetUsersQuery } from "../../redux/api/adminApi";
import { useGetBookingsQuery } from "../../redux/api/bookingApi";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const navigate = useNavigate();

  const { user } = useSelector((state) => state.auth);

  // API Hooks
  const { data: usersData } = useGetUsersQuery();
  const { data: bookingsData } = useGetBookingsQuery();

  const users = usersData?.users || [];
  const bookings = bookingsData?.bookings || [];

  /* -------------------------------------------------------------------------- */
  /*                               CALCULATED STATS                              */
  /* -------------------------------------------------------------------------- */

  // 1️⃣ Visitors = total users
  const visitorCount = users.length;

  // 2️⃣ Subscribers = bookings with status pending
  const pendingBookings = bookings.filter((b) => b.status === "pending");
  const pendingCount = pendingBookings.length;

  // 3️⃣ Total Sales = totalAmount of confirmed bookings
  const confirmedBookings = bookings.filter((b) => b.status === "confirmed");
  const totalSales = confirmedBookings.reduce(
    (sum, b) => sum + Number(b.totalAmount || 0),
    0
  );

  // 4️⃣ Current Month Sales
  const currentMonth = new Date().getMonth();
  const currentYear = new Date().getFullYear();

  const currentMonthSales = confirmedBookings
    .filter((b) => {
      const date = new Date(b.createdAt);
      return (
        date.getMonth() === currentMonth && date.getFullYear() === currentYear
      );
    })
    .reduce((sum, b) => sum + Number(b.totalAmount || 0), 0);

  // 5️⃣ Last 5 new users
  const newUsers = [...users]
    .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
    .slice(0, 5);

  // 6️⃣ Pending booking list for Transaction History
  const pendingBookingList = pendingBookings.slice(0, 10);

  /* -------------------------------------------------------------------------- */
  /*                                    UI                                       */
  /* -------------------------------------------------------------------------- */

  return (
    <>
      <MetaData title={"Dashboard"} />

      <div className="page-inner">
        <div className="d-flex align-items-left align-items-md-center flex-column flex-md-row pt-2 pb-4">
          <div>
            <h3 className="fw-bold mb-3">Dashboard</h3>
            <h6 className="op-7 mb-2">Aayo Admin Dashboard</h6>
          </div>
        </div>

        {/* ==================== TOP CARDS ==================== */}
        <div className="row">
          {/* Visitors */}
          <div className="col-sm-6 col-md-3">
            <Link to="/admin/users" style={{ textDecoration: "none" }}>
              <div className="card card-stats card-round">
                <div className="card-body">
                  <div className="row align-items-center">
                    <div className="col-icon">
                      <div className="icon-big text-center icon-primary bubble-shadow-small">
                        <i className="fas fa-users"></i>
                      </div>
                    </div>
                    <div className="col col-stats ms-3 ms-sm-0">
                      <div className="numbers">
                        <p className="card-category">Users</p>
                        <h4 className="card-title">{visitorCount}</h4>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </Link>
          </div>

          {/* Pending Bookings */}
          <div
            className="col-sm-6 col-md-3"
            onClick={() => navigate("/admin/bookings?status=pending")}
          >
            <Link
              to="/admin/bookings?status=pending"
              style={{ textDecoration: "none" }}
            >
              <div className="card card-stats card-round">
                <div className="card-body">
                  <div className="row align-items-center">
                    <div className="col-icon">
                      <div className="icon-big text-center icon-info bubble-shadow-small">
                        <i className="fas fa-user-check"></i>
                      </div>
                    </div>
                    <div className="col col-stats ms-3 ms-sm-0">
                      <div className="numbers">
                        <p className="card-category">Pending Bookings</p>
                        <h4 className="card-title">{pendingCount} </h4>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </Link>
          </div>

          {/* Total Sales */}
          <div className="col-sm-6 col-md-3">
            <Link
              to="/admin/bookings?status=confirmed"
              style={{ textDecoration: "none" }}
            >
              <div className="card card-stats card-round">
                <div className="card-body">
                  <div className="row align-items-center">
                    <div className="col-icon">
                      <div className="icon-big text-center icon-success bubble-shadow-small">
                        <i className="fas fa-luggage-cart"></i>
                      </div>
                    </div>
                    <div className="col col-stats ms-3 ms-sm-0">
                      <div className="numbers">
                        <p className="card-category">Total Sales</p>
                        <h4 className="card-title">
                          ₹{totalSales.toLocaleString()}
                        </h4>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </Link>
          </div>

          {/* Monthly Sales */}
          <div className="col-sm-6 col-md-3">
            <Link
              to="/admin/bookings?month=current"
              style={{ textDecoration: "none" }}
            >
              <div className="card card-stats card-round">
                <div className="card-body">
                  <div className="row align-items-center">
                    <div className="col-icon">
                      <div className="icon-big text-center icon-secondary bubble-shadow-small">
                        <i className="far fa-check-circle"></i>
                      </div>
                    </div>
                    <div className="col col-stats ms-3 ms-sm-0">
                      <div className="numbers">
                        <p className="card-category">Sales (This Month)</p>
                        <h4 className="card-title">
                          ₹{currentMonthSales.toLocaleString()}
                        </h4>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </Link>
          </div>
        </div>

        {/* ---------------------------------------------------- */}
        {/*  5. REMOVE User Statistics and Daily Sales SECTION   */}
        {/* ---------------------------------------------------- */}

        {/* REMOVED AS REQUESTED */}

        {/* ---------------------------------------------------- */}
        {/* 6. REMOVE Users Geolocation SECTION                   */}
        {/* ---------------------------------------------------- */}

        {/* REMOVED AS REQUESTED */}

        {/* ==================== NEW CUSTOMERS ==================== */}
        <div className="row mt-4">
          <div className="col-md-4">
            <div className="card card-round">
              <div className="card-body">
                <div className="card-head-row card-tools-still-right">
                  <h4 className="card-title">New Customers</h4>
                </div>

                <div className="card-list py-3">
                  {newUsers.map((u) => (
                    <Link
                      to={`/admin/users?search=${encodeURIComponent(
                        u.email
                      )}&open=true`}
                      style={{ textDecoration: "none", color: "inherit" }}
                    >
                      <div className="item-list d-flex align-items-center mb-3">
                        <div className="avatar">
                          <span className="avatar-title rounded-circle bg-primary text-white">
                            {u.name.charAt(0)}
                          </span>
                        </div>

                        <div className="info-user ms-3">
                          <div className="username">{u.name}</div>
                          <div className="status">{u.email}</div>
                        </div>
                      </div>
                    </Link>
                  ))}

                  {newUsers.length === 0 && (
                    <p className="text-muted">No new customers found.</p>
                  )}
                </div>
              </div>
            </div>
          </div>

          {/* ==================== PENDING BOOKINGS TABLE ==================== */}
          <div className="col-md-8">
            <div className="card card-round">
              <div className="card-header">
                <h4 className="card-title">Pending Bookings</h4>
              </div>

              <div className="card-body p-0">
                <div className="table-responsive">
                  <table className="table align-items-center mb-0">
                    <thead className="thead-light">
                      <tr>
                        <th>Booking Code</th>
                        <th className="text-end">Date & Time</th>
                        <th className="text-end">Amount</th>
                        <th className="text-end">Status</th>
                      </tr>
                    </thead>

                    <tbody>
                      {pendingBookingList.map((b) => (
                        <tr
                          key={b._id}
                          style={{ cursor: "pointer" }}
                          onClick={() =>
                            navigate(
                              `/admin/bookings?search=${encodeURIComponent(
                                b.bookingCode
                              )}&open=true`
                            )
                          }
                        >
                          <td>{b.bookingCode}</td>
                          <td className="text-end">
                            {b.travelDate} {b.travelTime}
                          </td>
                          <td className="text-end">
                            ₹{Number(b.totalAmount).toLocaleString()}
                          </td>
                          <td className="text-end">
                            <span className="badge badge-warning">Pending</span>
                          </td>
                        </tr>
                      ))}

                      {pendingBookingList.length === 0 && (
                        <tr>
                          <td colSpan="4" className="text-center py-3">
                            No pending bookings found.
                          </td>
                        </tr>
                      )}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Dashboard;
