import { useEffect, useState } from "react";
import axios from "axios";

export default function Masters({ type }) {
  const [items, setItems] = useState([]);
  const [label, setLabel] = useState("");

  const load = async () => {
    const res = await axios.get(`/masters?type=${type}`);
    setItems(res.data.masters);
  };

  useEffect(() => {
    load();
  }, [type]);

  const add = async () => {
    await axios.post("/masters", {
      type,
      label,
      value: label.toLowerCase().replace(/\s+/g, "_"),
    });
    setLabel("");
    load();
  };

  return (
    <div className="card p-4">
      <h4>{type} Master</h4>

      <input
        value={label}
        onChange={(e) => setLabel(e.target.value)}
        placeholder="Add new"
        className="form-control mb-2"
      />
      <button onClick={add} className="btn btn-primary mb-3">
        Add
      </button>

      <ul className="list-group">
        {items.map((i) => (
          <li key={i._id} className="list-group-item">
            {i.label}
          </li>
        ))}
      </ul>
    </div>
  );
}
