import React, { useState } from "react";
import { useGetUsersQuery } from "../../redux/api/adminApi";
import UserRow from "./UserRow";
import AddUserForm from "./AddUserForm";
import toast from "react-hot-toast";

const UserList = () => {
  const { data, isLoading, error, refetch } = useGetUsersQuery();
  const [showAddForm, setShowAddForm] = useState(false);

  if (error) return toast.error("Failed to load users");

  return (
    <div className="container mt-4">
      <h3>User Role Management</h3>

      <button
        className="btn btn-primary mb-3"
        onClick={() => setShowAddForm(!showAddForm)}
      >
        {showAddForm ? "Hide Add Form" : "Add New User"}
      </button>

      {showAddForm && (
        <AddUserForm onClose={() => setShowAddForm(false)} refetch={refetch} />
      )}

      <table className="table table-bordered table-hover mt-3">
        <thead className="table-dark">
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Current Role</th>
            <th>Change Role / Edit</th>
          </tr>
        </thead>
        <tbody>
          {data?.users?.map((user) => (
            <UserRow key={user._id} user={user} refetch={refetch} />
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserList;
