import React, { useEffect, useState } from "react";
import {
  useCreateFlightTypeMutation,
  useUpdateFlightTypeMutation,
} from "../../redux/api/flightTypeApi";
import toast from "react-hot-toast";
import MyEditorComponent from "../MyEditorComponent";
import { useGetFlightCategoriesQuery } from "../../redux/api/flightCategoryApi";
import Select from "react-select";
import { airports, regions, countries } from "airports-json";

const FlightTypeForm = ({ editingFlight, setShowForm, refetch }) => {
  const [form, setForm] = useState({
    name: "",
    capacity: "",
    description: "",
    maxSpeed: "",
    status: true,
    baggage: "",
    baseLocation: "", // Will store IATA code instead of ID
    pilots: "",
    flightAttendant: false,
    flightCategory: "",
  });

  //const [airports, setAirports] = useState([]); // Local JSON airports
  const [existingImages, setExistingImages] = useState([]);
  const [newImages, setNewImages] = useState([]);
  const [previewImages, setPreviewImages] = useState([]);
  const [removedImageIds, setRemovedImageIds] = useState([]);

  const [createFlightType] = useCreateFlightTypeMutation();
  const [updateFlightType] = useUpdateFlightTypeMutation();
  const { data: categoryData, isLoading: categoriesLoading } =
    useGetFlightCategoriesQuery();

  // ✅ Load airports from local JSON file
  // useEffect(() => {
  //   fetch("/data/airports.json")
  //     .then((res) => res.json())
  //     .then((data) => setAirports(data))
  //     .catch(() => toast.error("Failed to load airport list"));
  // }, []);

  // ✅ If editing, pre-fill form
  useEffect(() => {
    if (editingFlight) {
      setForm({
        name: editingFlight.name,
        capacity: editingFlight.capacity,
        description: editingFlight.description,
        maxSpeed: editingFlight.maxSpeed,
        status: editingFlight.status,
        baggage: editingFlight.baggage || "",
        baseLocation: editingFlight.baseLocation || "", // IATA code
        pilots: editingFlight.pilots || "",
        flightAttendant: editingFlight.flightAttendant || false,
        flightCategory: editingFlight.flightCategory?._id || "",
      });
      setExistingImages(editingFlight.images || []);
    }
  }, [editingFlight]);

  // ✅ Manage image previews
  useEffect(() => {
    const previews = [
      ...existingImages.map((img) => ({ type: "existing", src: img.url })),
      ...newImages.map((item) => ({ type: "new", src: item.preview })),
    ];
    setPreviewImages(previews);
  }, [existingImages, newImages]);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === "file") {
      const selectedFiles = Array.from(files).map((file) => ({
        file,
        preview: URL.createObjectURL(file),
      }));
      setNewImages([...newImages, ...selectedFiles]);
    } else {
      setForm((prev) => ({
        ...prev,
        [name]: type === "checkbox" ? checked : value,
      }));
    }
  };

  const removeImage = (index) => {
    const imgToRemove = previewImages[index];
    if (imgToRemove.type === "existing") {
      const removedImage = existingImages.find(
        (img) => img.url === imgToRemove.src
      );
      if (removedImage) {
        setRemovedImageIds([...removedImageIds, removedImage.public_id]);
        setExistingImages(
          existingImages.filter((img) => img.url !== imgToRemove.src)
        );
      }
    } else {
      setNewImages(newImages.filter((img) => img.preview !== imgToRemove.src));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.entries(form).forEach(([key, value]) => {
      formData.append(key, value);
    });

    existingImages.forEach((img) =>
      formData.append("existingImages[]", img.url)
    );
    removedImageIds.forEach((id) => formData.append("removedImages[]", id));
    newImages.forEach((imgObj) => formData.append("images", imgObj.file));

    try {
      if (editingFlight) {
        await updateFlightType({
          id: editingFlight._id,
          formData,
        }).unwrap();
        toast.success("Flight type updated");
      } else {
        await createFlightType(formData).unwrap();
        toast.success("Flight type created");
      }
      setShowForm(false);
      refetch();
    } catch (err) {
      toast.error("Failed to save flight type");
    }
  };

  // ✅ Prepare airport dropdown options (IATA-based)
  const airportOptions = airports.map((airport) => ({
    value: airport.iata_code, // Save IATA code
    label: `${airport.name} (${airport.iata_code}) - ${airport.municipality}`,
  }));

  return (
    <div className="card p-3 mb-4">
      <form
        className="border p-4 mb-4"
        onSubmit={handleSubmit}
        encType="multipart/form-data"
      >
        <h5>{editingFlight ? "Edit" : "Add"} Flight Type</h5>
        <div className="row">
          <div className="col-md-4 mb-3">
            <input
              type="text"
              name="name"
              placeholder="Flight Name"
              className="form-control"
              value={form.name}
              onChange={handleChange}
              required
            />
          </div>

          <div className="col-md-4 mb-4">
            <select
              name="flightCategory"
              className="form-control"
              value={form.flightCategory}
              onChange={handleChange}
              required
            >
              <option value="">Select Flight Category</option>
              {!categoriesLoading &&
                categoryData?.flightCategories?.map((cat) => (
                  <option key={cat._id} value={cat._id}>
                    {cat.name}
                  </option>
                ))}
            </select>
          </div>

          <div className="col-md-4 mb-4">
            <Select
              options={airportOptions}
              value={
                airportOptions.find(
                  (airport) => airport.value === form.baseLocation
                ) || null
              }
              onChange={(selected) =>
                setForm((prev) => ({
                  ...prev,
                  baseLocation: selected?.value || "",
                }))
              }
              placeholder="Select Base Airport (IATA)"
              isClearable
              isSearchable
              required
            />
          </div>

          <div className="col-md-4 mb-4">
            <input
              type="number"
              name="capacity"
              placeholder="Capacity (Pax)"
              className="form-control"
              value={form.capacity}
              onChange={handleChange}
              required
            />
          </div>

          <div className="col-md-4 mb-4">
            <input
              type="number"
              name="maxSpeed"
              placeholder="Max Speed (km/h)"
              className="form-control"
              value={form.maxSpeed}
              onChange={handleChange}
              required
            />
          </div>

          <div className="col-md-4 mb-4">
            <input
              type="number"
              name="baggage"
              placeholder="Baggage Allowance (kg)"
              className="form-control"
              value={form.baggage}
              onChange={handleChange}
              required
            />
          </div>

          <div className="col-md-4 mb-4">
            <input
              type="number"
              name="pilots"
              placeholder="Pilots"
              className="form-control"
              value={form.pilots}
              onChange={handleChange}
            />
          </div>

          <div className="col-md-4 mb-4 d-flex align-items-center">
            <label className="form-check-label me-2">Flight Attendant:</label>
            <input
              type="checkbox"
              name="flightAttendant"
              checked={form.flightAttendant}
              onChange={handleChange}
            />
          </div>

          <div className="col-md-12 mb-4">
            <MyEditorComponent
              value={form.description}
              onChange={(value) =>
                setForm((prev) => ({ ...prev, description: value }))
              }
            />
          </div>

          <div className="col-md-6 mb-4">
            <label className="form-label">Upload Flight Images</label>
            <input
              type="file"
              name="images"
              className="form-control"
              accept="image/*"
              onChange={handleChange}
              multiple
              required={!editingFlight}
            />
            {previewImages.length > 0 &&
              previewImages.map((img, idx) => (
                <div className="col-md-2 mb-3 position-relative" key={idx}>
                  <img
                    src={img.src}
                    alt={`Preview ${idx}`}
                    width="100"
                    className="rounded"
                  />
                  <button
                    type="button"
                    className="btn btn-sm btn-danger position-absolute top-0 end-0 translate-middle"
                    style={{ borderRadius: "50%" }}
                    onClick={() => removeImage(idx)}
                  >
                    <i className="bi bi-x"></i>
                  </button>
                </div>
              ))}
          </div>

          <div className="col-md-6 mb-4 d-flex align-items-center">
            <label className="form-check-label me-2" htmlFor="status">
              Active:
            </label>
            <input
              type="checkbox"
              id="status"
              name="status"
              checked={form.status}
              onChange={handleChange}
            />
          </div>
        </div>

        <button className="btn btn-success">
          {editingFlight ? "Update" : "Save"}
        </button>
        <button
          className="btn btn-secondary ms-2"
          type="button"
          onClick={() => setShowForm(false)}
        >
          Cancel
        </button>
      </form>
    </div>
  );
};

export default FlightTypeForm;
