const ModelMediaApproval = ({ model, onApprove }) => {
  return (
    <>
      <h5>Profile Image</h5>
      {model.profileImage && (
        <div>
          <img src={model.profileImage.url} width={150} />
          <button
            className={`btn btn-sm ${
              model.profileImage.approved ? "btn-success" : "btn-warning"
            }`}
            onClick={() =>
              onApprove("profileImage", !model.profileImage.approved)
            }
          >
            {model.profileImage.approved ? "Approved" : "Approve"}
          </button>
        </div>
      )}

      <h5 className="mt-3">Portfolio</h5>
      <div className="d-flex flex-wrap gap-3">
        {model.portfolio?.map((img, i) => (
          <div key={i}>
            <img src={img.url} width={120} />
            <button
              className={`btn btn-sm ${
                img.approved ? "btn-success" : "btn-warning"
              }`}
              onClick={() =>
                onApprove(`portfolio.${i}.approved`, !img.approved)
              }
            >
              {img.approved ? "Approved" : "Approve"}
            </button>
          </div>
        ))}
      </div>
    </>
  );
};
export default ModelMediaApproval;
