import React, { useEffect, useState } from "react";
import {
  useCreateFlightTypeMutation,
  useUpdateFlightTypeMutation,
  useGetFlightTypeQuery,
} from "../../redux/api/flightApi";
import { useParams } from "react-router-dom";

const FlightTypeForm = () => {
  const { id } = useParams(); // for edit mode
  const isEditMode = Boolean(id);

  const [form, setForm] = useState({
    name: "",
    capacity: "",
    description: "",
    maxSpeed: "",
    status: true,
    images: [],
  });

  const [previewImages, setPreviewImages] = useState([]);
  const [existingImages, setExistingImages] = useState([]);
  const [removedImageIds, setRemovedImageIds] = useState([]);

  const [createFlightType] = useCreateFlightTypeMutation();
  const [updateFlightType] = useUpdateFlightTypeMutation();
  const { data: flightTypeData } = useGetFlightTypeQuery(id, { skip: !id });

  useEffect(() => {
    if (flightTypeData?.flightType) {
      const { name, capacity, description, maxSpeed, status, images } =
        flightTypeData.flightType;
      setForm({ name, capacity, description, maxSpeed, status, images: [] });
      setExistingImages(images);
    }
  }, [flightTypeData]);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;

    if (type === "file") {
      const selectedFiles = Array.from(files);
      setForm((prev) => ({ ...prev, images: selectedFiles }));
      setPreviewImages(selectedFiles.map((file) => URL.createObjectURL(file)));
    } else {
      setForm((prev) => ({
        ...prev,
        [name]: type === "checkbox" ? checked : value,
      }));
    }
  };

  const handleExistingImageRemove = (public_id) => {
    setRemovedImageIds((prev) => [...prev, public_id]);
    setExistingImages((prev) =>
      prev.filter((img) => img.public_id !== public_id)
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    Object.entries(form).forEach(([key, value]) => {
      if (key !== "images") formData.append(key, value);
    });

    form.images.forEach((file) => {
      formData.append("images", file);
    });

    if (removedImageIds.length > 0) {
      removedImageIds.forEach((id) => formData.append("removedImageIds[]", id));
    }

    try {
      if (isEditMode) {
        await updateFlightType({ id, data: formData }).unwrap();
      } else {
        await createFlightType(formData).unwrap();
      }
      alert("Flight Type saved successfully");
    } catch (error) {
      console.error("Error saving flight type:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit} encType="multipart/form-data">
      <div className="row">
        <div className="col-md-6 mb-3">
          <label>Name</label>
          <input
            type="text"
            name="name"
            className="form-control"
            value={form.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="col-md-6 mb-3">
          <label>Capacity</label>
          <input
            type="number"
            name="capacity"
            className="form-control"
            value={form.capacity}
            onChange={handleChange}
            required
          />
        </div>

        <div className="col-md-6 mb-3">
          <label>Max Speed</label>
          <input
            type="text"
            name="maxSpeed"
            className="form-control"
            value={form.maxSpeed}
            onChange={handleChange}
          />
        </div>

        <div className="col-md-6 mb-3">
          <label>Status</label>
          <select
            name="status"
            className="form-select"
            value={form.status}
            onChange={handleChange}
          >
            <option value={true}>Active</option>
            <option value={false}>Inactive</option>
          </select>
        </div>

        <div className="col-md-12 mb-3">
          <label>Description</label>
          <textarea
            name="description"
            className="form-control"
            value={form.description}
            onChange={handleChange}
            rows="3"
          ></textarea>
        </div>

        <div className="col-md-6 mb-3">
          <label>Images</label>
          <input
            type="file"
            name="images"
            className="form-control"
            accept="image/*"
            onChange={handleChange}
            multiple
          />
        </div>

        {/* Existing Images */}
        {existingImages.length > 0 &&
          existingImages.map((img, idx) => (
            <div
              className="col-md-2 mb-3 position-relative"
              key={img.public_id}
            >
              <img
                src={img.url}
                alt={`Image ${idx}`}
                width="100"
                className="rounded"
              />
              <button
                type="button"
                className="btn btn-sm btn-danger position-absolute top-0 end-0"
                onClick={() => handleExistingImageRemove(img.public_id)}
                style={{ borderRadius: "50%", padding: "2px 6px" }}
              >
                &times;
              </button>
            </div>
          ))}

        {/* Preview New Images */}
        {previewImages.length > 0 &&
          previewImages.map((src, idx) => (
            <div className="col-md-2 mb-3" key={idx}>
              <img
                src={src}
                alt={`Preview ${idx}`}
                width="100"
                className="rounded"
              />
            </div>
          ))}

        <div className="col-md-12">
          <button type="submit" className="btn btn-primary">
            {isEditMode ? "Update Flight Type" : "Create Flight Type"}
          </button>
        </div>
      </div>
    </form>
  );
};

export default FlightTypeForm;
