// src/redux/slices/authSlice.js (or wherever it is)

import { createSlice } from '@reduxjs/toolkit';
import { authApi } from '../api/authApi'; // ← Make sure this import path is correct!

const authSlice = createSlice({
    name: 'auth',
    initialState: {
        user: null,
        token: null,
        isAuthenticated: false,
    },
    reducers: {
        logout: (state) => {
            state.user = null;
            state.token = null;
            state.isAuthenticated = false;
            localStorage.removeItem('authToken'); // or 'token' if that's your key
        },
    },
    extraReducers: (builder) => {
        builder
            // THIS IS WHAT WAS MISSING – updates state on successful login
            .addMatcher(authApi.endpoints.login.matchFulfilled, (state, { payload }) => {
                console.log('Login fulfilled – updating auth state:', payload); // Debug log

                state.token = payload.token;
                state.user = payload.user; // { id, role, name }
                state.isAuthenticated = true;

                // Save token for page refresh
                localStorage.setItem('authToken', payload.token);
            })
            // Optional: handle error
            .addMatcher(authApi.endpoints.login.matchRejected, (state) => {
                state.isAuthenticated = false;
                state.user = null;
                state.token = null;
            });
    },
});

export const { logout } = authSlice.actions;
export default authSlice.reducer;
