import React, { useState } from "react";
import {
  useGetFlightsQuery,
  useDeleteFlightMutation,
  useToggleFlightStatusMutation,
} from "../../redux/api/flightApi";
import toast from "react-hot-toast";
import FlightForm from "./FlightForm";

const FlightList = () => {
  const { data, isLoading } = useGetFlightsQuery();
  const [deleteFlight] = useDeleteFlightMutation();
  const [toggleStatus] = useToggleFlightStatusMutation();

  const [selectedFlight, setSelectedFlight] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure?")) {
      await deleteFlight(id).unwrap();
      toast.success("Deleted");
    }
  };

  const handleToggleStatus = async (id) => {
    await toggleStatus(id).unwrap();
  };

  if (isLoading) return <p>Loading...</p>;

  return (
    <div className="container mt-3">
      <div className="d-flex justify-content-between">
        <h3>Flights</h3>
        <button
          className="btn btn-success"
          onClick={() => {
            setSelectedFlight(null);
            setShowForm(true);
          }}
        >
          Add Flight
        </button>
      </div>
      {showForm && (
        <FlightForm
          selectedFlight={selectedFlight}
          onClose={() => setShowForm(false)}
        />
      )}

      <table className="table table-bordered mt-3">
        <thead>
          <tr>
            <th>Flight Name</th>
            <th>From</th>
            <th>To</th>
            <th>Date</th>
            <th>Time</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data?.flights?.map((flight) => (
            <tr key={flight._id}>
              <td>{flight.flightName}</td>
              <td>{flight.from}</td>
              <td>{flight.to}</td>
              <td>{flight.date.split("T")[0]}</td>
              <td>{flight.time}</td>
              <td>
                <button
                  className={`btn btn-sm ${
                    flight.status === "active" ? "btn-success" : "btn-secondary"
                  }`}
                  onClick={() => handleToggleStatus(flight._id)}
                >
                  {flight.status}
                </button>
              </td>
              <td>
                <button
                  className="btn btn-primary btn-sm me-2"
                  onClick={() => {
                    setSelectedFlight(flight);
                    setShowForm(true);
                  }}
                >
                  Edit
                </button>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDelete(flight._id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default FlightList;
