import React, { useState, useEffect } from "react";
import {
  useCreateFlightMutation,
  useUpdateFlightMutation,
} from "../../redux/api/flightApi";
import toast from "react-hot-toast";

const FlightForm = ({ selectedFlight, onClose }) => {
  const [formData, setFormData] = useState({
    flightName: "",
    from: "",
    to: "",
    date: "",
    time: "",
  });

  useEffect(() => {
    if (selectedFlight) {
      setFormData({
        flightName: selectedFlight.flightName,
        from: selectedFlight.from,
        to: selectedFlight.to,
        date: selectedFlight.date.split("T")[0],
        time: selectedFlight.time,
      });
    }
  }, [selectedFlight]);

  const [createFlight] = useCreateFlightMutation();
  const [updateFlight] = useUpdateFlightMutation();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (selectedFlight) {
        await updateFlight({ id: selectedFlight._id, data: formData }).unwrap();
        toast.success("Flight updated");
      } else {
        await createFlight(formData).unwrap();
        toast.success("Flight created");
      }
      onClose();
    } catch (error) {
      toast.error("Error submitting flight");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="card p-3">
      <div className="mb-2">
        <label>Flight Name</label>
        <input
          type="text"
          className="form-control"
          value={formData.flightName}
          onChange={(e) =>
            setFormData({ ...formData, flightName: e.target.value })
          }
          required
        />
      </div>
      <div className="mb-2">
        <label>From</label>
        <input
          type="text"
          className="form-control"
          value={formData.from}
          onChange={(e) => setFormData({ ...formData, from: e.target.value })}
          required
        />
      </div>
      <div className="mb-2">
        <label>To</label>
        <input
          type="text"
          className="form-control"
          value={formData.to}
          onChange={(e) => setFormData({ ...formData, to: e.target.value })}
          required
        />
      </div>
      <div className="mb-2">
        <label>Date</label>
        <input
          type="date"
          className="form-control"
          value={formData.date}
          onChange={(e) => setFormData({ ...formData, date: e.target.value })}
          required
        />
      </div>
      <div className="mb-2">
        <label>Time</label>
        <input
          type="time"
          className="form-control"
          value={formData.time}
          onChange={(e) => setFormData({ ...formData, time: e.target.value })}
          required
        />
      </div>
      <button className="btn btn-primary mt-2" type="submit">
        {selectedFlight ? "Update" : "Add"} Flight
      </button>
    </form>
  );
};

export default FlightForm;
