import React, { useEffect, useState, useRef } from "react";
import {
  useCreateBookingMutation,
  useUpdateBookingMutation,
} from "../../redux/api/bookingApi";

import { useGetUsersQuery } from "../../redux/api/adminApi";
import { useGetFlightTypesQuery } from "../../redux/api/flightTypeApi";
import { useGetAirportsQuery } from "../../redux/api/airportApi"; // ✅ NEW – airport from DB
import { useGetEmptyLegsQuery } from "../../redux/api/emptyLegApi";

import AsyncSelect from "react-select/async";
import Select from "react-select";
import toast from "react-hot-toast";
import EmptyLegModal from "../bookings/EmptyLegModal";

const AddBookingForm = ({ setShowForm, refetch, booking }) => {
  const formRef = useRef(null);

  const [form, setForm] = useState({
    user: "",
    fromPlace: "",
    toPlace: "",
    travelDate: "",
    travelTime: "",
    returnDate: "",
    returnTime: "",
    flightType: "",
    pax: "",
    status: "pending",
    remarks: "",
    totalAmount: "",
    bookingType: "normal",
    tripType: "oneway",
    emptyLegId: "",
  });

  const [showEmptyLegModal, setShowEmptyLegModal] = useState(false);
  const [emptyLegsModalList, setEmptyLegsModalList] = useState([]);

  const { data: users } = useGetUsersQuery();
  const { data: flightTypes } = useGetFlightTypesQuery();
  const { data: airportsData } = useGetAirportsQuery(); // ✅ From DB
  const { data: emptyLegsData } = useGetEmptyLegsQuery();

  const [createBooking, { isLoading: isCreating }] = useCreateBookingMutation();
  const [updateBooking, { isLoading: isUpdating }] = useUpdateBookingMutation();

  const isLoading = isCreating || isUpdating;

  /* -------------------------------------------------------------------------- */
  /*                           PRE-FILL WHEN EDITING                            */
  /* -------------------------------------------------------------------------- */

  useEffect(() => {
    if (booking) {
      setForm({
        user: booking.user?._id || "",
        fromPlace: booking.fromPlace || "",
        toPlace: booking.toPlace || "",
        travelDate: booking.travelDate || "",
        travelTime: booking.travelTime || "",
        returnDate: booking.returnDate || "",
        returnTime: booking.returnTime || "",
        flightType: booking.flightType?._id || "",
        pax: booking.pax || "",
        status: booking.status || "pending",
        remarks: booking.remarks || "",
        totalAmount: booking.totalAmount || "",
        bookingType: booking.bookingType || "normal",
        tripType: booking.tripType || "oneway",
        emptyLegId: booking.emptyLegId || "",
      });

      formRef.current?.scrollIntoView({ behavior: "smooth" });
    }
  }, [booking]);

  /* -------------------------------------------------------------------------- */
  /*                            AIRPORT SEARCH (DB)                             */
  /* -------------------------------------------------------------------------- */

  // airports from API
  const airports = airportsData?.airports || [];

  const loadAirportOptions = (inputValue, callback) => {
    if (!inputValue) {
      callback([]);
      return;
    }

    const filtered = airports
      .filter(
        (a) =>
          a.name.toLowerCase().includes(inputValue.toLowerCase()) ||
          a.municipality.toLowerCase().includes(inputValue.toLowerCase()) ||
          a.iata_code.toLowerCase().includes(inputValue.toLowerCase())
      )
      .slice(0, 20)
      .map((a) => ({
        value: a.iata_code,
        label: `${a.name} (${a.iata_code}) - ${a.municipality}`,
      }));

    callback(filtered);
  };

  /* -------------------------------------------------------------------------- */
  /*                              EMPTY LEG OPTIONS                              */
  /* -------------------------------------------------------------------------- */

  //   const emptyLegsOptions =
  //     emptyLegsData?.emptyLegs?.map((leg) => ({
  //       value: leg._id,
  //       label: `${leg.from} → ${leg.to} — ₹${Number(
  //         leg.offerPrice || leg.exactPrice || 0
  //       ).toLocaleString()}`,
  //       leg,
  //     })) || [];

  // ------------------ emptyLegsOptions (replace existing) ------------------
  const emptyLegsOptions =
    (emptyLegsData?.emptyLegs || []).map((leg) => {
      // adjust this detection to whatever your API uses:
      // e.g. leg.isBooked, leg.status === 'confirmed', leg.booked, leg.bookingId, etc.
      const isTaken =
        Boolean(leg.isBooked) ||
        String(leg.status).toLowerCase() === "confirmed" ||
        Boolean(leg.booked) ||
        Boolean(leg.bookingId);
      return {
        value: leg._id,
        label: `${leg.from} → ${leg.to} — ₹${Number(
          leg.offerPrice || leg.exactPrice || 0
        ).toLocaleString()}${isTaken ? " (Unavailable)" : ""}`,
        leg,
        disabled: isTaken, // used by react-select's isOptionDisabled below
      };
    }) || [];
  // ------------------------------------------------------------------------

  // ------------------ handleEmptyLegSelect (replace existing) ------------------
  const handleEmptyLegSelect = (option) => {
    if (!option) {
      // clear selection and the auto-filled fields
      setForm((prev) => ({
        ...prev,
        emptyLegId: "",
        fromPlace: "",
        toPlace: "",
        travelDate: "",
        travelTime: "",
        flightType: "",
        totalAmount: "",
        bookingType: "normal",
      }));
      return;
    }

    if (option.disabled) {
      // disabled (unavailable) option clicked — ignore and notify
      toast.error("This empty leg is no longer available.");
      return;
    }

    const leg = option.leg;
    setForm((prev) => ({
      ...prev,
      emptyLegId: leg._id,
      bookingType: "empty_legs",

      // AUTO-FILL the fields for admin convenience
      fromPlace: leg.from,
      toPlace: leg.to,
      travelDate: leg.date,
      travelTime: leg.time,
      flightType: leg.flightType?._id || prev.flightType,
      totalAmount: leg.offerPrice || leg.exactPrice || prev.totalAmount,
    }));
  };
  // -------------------------------------------------------------------------------

  //   const handleEmptyLegSelect = (option) => {
  //     if (!option) {
  //       setForm((prev) => ({
  //         ...prev,
  //         emptyLegId: "",
  //         // CLEAR AUTOFILLED FIELDS
  //         fromPlace: "",
  //         toPlace: "",
  //         travelDate: "",
  //         travelTime: "",
  //         flightType: "",
  //         totalAmount: "",
  //       }));
  //       return;
  //     }

  //     const leg = option.leg;

  //     setForm((prev) => ({
  //       ...prev,
  //       emptyLegId: leg._id,
  //       bookingType: "empty_legs",

  //       // ✅ AUTO-FILL FIELDS
  //       fromPlace: leg.from,
  //       toPlace: leg.to,
  //       travelDate: leg.date,
  //       travelTime: leg.time,
  //       flightType: leg.flightType?._id || prev.flightType,
  //       totalAmount: leg.offerPrice || leg.exactPrice || prev.totalAmount,
  //     }));
  //   };

  //   const handleEmptyLegSelect = (option) => {
  //     if (!option) {
  //       setForm((prev) => ({
  //         ...prev,
  //         emptyLegId: "",
  //       }));
  //       return;
  //     }

  //     // Admin-selected empty-leg SHOULD NOT autofill anything
  //     setForm((prev) => ({
  //       ...prev,
  //       emptyLegId: option.value,
  //       bookingType: "empty_legs",
  //     }));
  //   };

  /* -------------------------------------------------------------------------- */
  /*                                FORM SUBMIT                                  */
  /* -------------------------------------------------------------------------- */

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.bookingType === "empty_legs" && !form.emptyLegId) {
      toast.error("Please select an Empty Leg deal.");
      return;
    }

    const requiredFields = [
      "user",
      "fromPlace",
      "toPlace",
      "travelDate",
      "travelTime",
      "flightType",
      "pax",
      "totalAmount",
    ];

    for (const key of requiredFields) {
      if (!form[key]) {
        toast.error("Please fill all required fields.");
        return;
      }
    }

    if (form.tripType === "round" && (!form.returnDate || !form.returnTime)) {
      toast.error("Please enter return date/time for round trip");
      return;
    }

    try {
      const payload = {
        ...form,
        emptyLegId: form.emptyLegId || null,
        totalAmount: parseFloat(form.totalAmount),
      };

      let res;

      if (booking?._id) {
        res = await updateBooking({ id: booking._id, data: payload }).unwrap();

        // Show empty-leg suggestions ONLY for NORMAL bookings
        if (
          form.bookingType === "normal" &&
          res?.emptyLegs &&
          res.emptyLegs.length > 0
        ) {
          setEmptyLegsModalList(res.emptyLegs);
          setShowEmptyLegModal(true);
          return;
        }
      } else {
        await createBooking(payload).unwrap();
      }

      toast.success(`Booking ${booking ? "updated" : "created"} successfully`);
      refetch();
      setShowForm(false);
    } catch (err) {
      console.error("Error submitting form", err);
      toast.error(err?.data?.message || "Failed to create booking");
    }
  };

  /* -------------------------------------------------------------------------- */
  /*                                  RENDER                                     */
  /* -------------------------------------------------------------------------- */

  return (
    <>
      {isLoading && (
        <div className="loader-overlay">
          <div className="spinner-border text-primary" role="status"></div>
        </div>
      )}

      <form
        ref={formRef}
        onSubmit={handleSubmit}
        className="border p-3 mb-4 bg-light"
      >
        <div className="row g-3">
          {/* Traveler */}
          <div className="col-md-4">
            <label>User</label>
            <Select
              options={
                users?.users?.map((u) => ({
                  value: u._id,
                  label: `${u.name} (${u.email})`,
                })) || []
              }
              value={
                users?.users
                  ?.map((u) => ({
                    value: u._id,
                    label: `${u.name} (${u.email})`,
                  }))
                  .find((opt) => opt.value === form.user) || null
              }
              onChange={(s) => setForm((p) => ({ ...p, user: s?.value || "" }))}
              isClearable
              isSearchable
              required
            />
          </div>

          {/* Trip Type */}
          <div className="col-md-4">
            <label>Trip Type</label>
            <select
              className="form-control"
              value={form.tripType}
              onChange={(e) =>
                setForm((prev) => ({ ...prev, tripType: e.target.value }))
              }
            >
              <option value="oneway">One Way</option>
              <option value="round">Round Trip</option>
            </select>
          </div>

          {/* Pax */}
          <div className="col-md-4">
            <label>Pax</label>
            <input
              type="number"
              className="form-control"
              value={form.pax}
              onChange={(e) =>
                setForm((prev) => ({ ...prev, pax: e.target.value }))
              }
              required
            />
          </div>

          {/* Booking Type */}
          <div className="col-md-12">
            <label>Booking Type</label>
            <select
              className="form-control"
              value={form.bookingType}
              onChange={(e) =>
                setForm((prev) => ({
                  ...prev,
                  bookingType: e.target.value,
                  emptyLegId:
                    e.target.value === "normal" ? "" : prev.emptyLegId,
                }))
              }
            >
              <option value="normal">Normal</option>
              <option value="empty_legs">Empty Legs</option>
            </select>

            {/* <select
              className="form-control"
              value={form.bookingType}
              onChange={(e) =>
                setForm((prev) => ({ ...prev, bookingType: e.target.value }))
              }
            >
              <option value="normal">Normal</option>
              <option value="empty_legs">Empty Legs</option>
            </select> */}
          </div>

          {/* Empty leg selector (no autofill) */}
          {form.bookingType === "empty_legs" && (
            <div className="col-md-12">
              <label>Select Empty Leg Deal</label>
              <Select
                options={emptyLegsOptions}
                onChange={handleEmptyLegSelect}
                isClearable
                value={
                  emptyLegsOptions.find((o) => o.value === form.emptyLegId) ||
                  null
                }
                isOptionDisabled={(opt) => opt.disabled === true}
              />
            </div>
          )}

          {/* FROM Airport */}
          <div className="col-md-6">
            <label>From Airport</label>
            <AsyncSelect
              cacheOptions
              loadOptions={loadAirportOptions}
              defaultOptions={false}
              value={
                form.fromPlace
                  ? {
                      value: form.fromPlace,
                      label: `${
                        airports.find((a) => a.iata_code === form.fromPlace)
                          ?.name
                      } (${form.fromPlace})`,
                    }
                  : null
              }
              onChange={(s) =>
                setForm((prev) => ({ ...prev, fromPlace: s?.value || "" }))
              }
              isClearable
            />
          </div>

          {/* TO Airport */}
          <div className="col-md-6">
            <label>To Airport</label>
            <AsyncSelect
              cacheOptions
              loadOptions={loadAirportOptions}
              defaultOptions={false}
              value={
                form.toPlace
                  ? {
                      value: form.toPlace,
                      label: `${
                        airports.find((a) => a.iata_code === form.toPlace)?.name
                      } (${form.toPlace})`,
                    }
                  : null
              }
              onChange={(s) =>
                setForm((prev) => ({ ...prev, toPlace: s?.value || "" }))
              }
              isClearable
            />
          </div>

          {/* Departure Date/Time */}
          <div className="col-md-6">
            <label>Departure Date</label>
            <input
              type="date"
              className="form-control"
              value={form.travelDate}
              onChange={(e) =>
                setForm((prev) => ({ ...prev, travelDate: e.target.value }))
              }
              required
            />
          </div>

          <div className="col-md-6">
            <label>Departure Time</label>
            <input
              type="time"
              className="form-control"
              value={form.travelTime}
              onChange={(e) =>
                setForm((prev) => ({ ...prev, travelTime: e.target.value }))
              }
              required
            />
          </div>

          {/* Return date/time */}
          {form.tripType === "round" && (
            <>
              <div className="col-md-6">
                <label>Return Date</label>
                <input
                  type="date"
                  className="form-control"
                  value={form.returnDate}
                  onChange={(e) =>
                    setForm((prev) => ({ ...prev, returnDate: e.target.value }))
                  }
                />
              </div>

              <div className="col-md-6">
                <label>Return Time</label>
                <input
                  type="time"
                  className="form-control"
                  value={form.returnTime}
                  onChange={(e) =>
                    setForm((prev) => ({ ...prev, returnTime: e.target.value }))
                  }
                />
              </div>
            </>
          )}

          {/* Flight Type */}
          <div className="col-md-6">
            <label>Flight Type</label>
            <Select
              options={
                flightTypes?.flightTypes?.map((f) => ({
                  value: f._id,
                  label: `${f.name} (${f.capacity} seats)`,
                })) || []
              }
              value={
                flightTypes?.flightTypes
                  ?.map((f) => ({
                    value: f._id,
                    label: `${f.name} (${f.capacity} seats)`,
                  }))
                  .find((opt) => opt.value === form.flightType) || null
              }
              onChange={(s) =>
                setForm((prev) => ({ ...prev, flightType: s?.value || "" }))
              }
              isClearable
              required
            />
          </div>

          {/* Amount */}
          <div className="col-md-6">
            <label>Total Amount</label>
            <input
              type="number"
              className="form-control"
              value={form.totalAmount}
              onChange={(e) =>
                setForm((prev) => ({ ...prev, totalAmount: e.target.value }))
              }
              required
            />
          </div>

          {/* Remarks */}
          <div className="col-md-12">
            <label>Remarks</label>
            <textarea
              className="form-control"
              rows="2"
              value={form.remarks}
              onChange={(e) =>
                setForm((prev) => ({ ...prev, remarks: e.target.value }))
              }
            />
          </div>

          {/* Status */}
          <div className="col-md-6">
            <label>Status</label>
            <select
              className="form-control"
              value={form.status}
              onChange={(e) =>
                setForm((prev) => ({ ...prev, status: e.target.value }))
              }
            >
              <option value="pending">Pending</option>
              <option value="confirmed_payment_pending">
                Confirmed (Payment Pending)
              </option>
              <option value="confirmed">Confirmed</option>
              <option value="cancelled">Cancelled</option>
            </select>
          </div>

          {/* Buttons */}
          <div className="col-md-12 d-flex gap-2 mt-3">
            <button
              type="submit"
              className="btn btn-success"
              disabled={isLoading}
            >
              {isLoading
                ? "Processing..."
                : booking
                ? "Update Booking"
                : "Create Booking"}
            </button>

            <button
              type="button"
              className="btn btn-secondary"
              onClick={() => setShowForm(false)}
              disabled={isLoading}
            >
              Cancel
            </button>
          </div>
        </div>
      </form>

      {showEmptyLegModal && (
        <EmptyLegModal
          legs={emptyLegsModalList}
          onClose={() => {
            setShowEmptyLegModal(false);
            setShowForm(false);
          }}
        />
      )}
    </>
  );
};

export default AddBookingForm;
