import React, { useEffect, useState } from "react";
import {
  useCreateFlightTypeMutation,
  useUpdateFlightTypeMutation,
} from "../../redux/api/flightTypeApi";
import toast from "react-hot-toast";

const FlightTypeForm = ({ editingFlight, setShowForm, refetch }) => {
  const [form, setForm] = useState({
    name: "",
    capacity: "",
    description: "",
    maxSpeed: "",
    status: true,
    images: [],
  });

  const [previewImages, setPreviewImages] = useState([]);
  const [selectedImages, setSelectedImages] = useState([]);

  const [createFlightType] = useCreateFlightTypeMutation();
  const [updateFlightType] = useUpdateFlightTypeMutation();

  useEffect(() => {
    if (editingFlight) {
      setForm({
        name: editingFlight.name,
        capacity: editingFlight.capacity,
        description: editingFlight.description,
        maxSpeed: editingFlight.maxSpeed,
        status: editingFlight.status,
        images: [],
      });
      setPreviewImages(editingFlight.images?.map((img) => img.url) || []);
    }
  }, [editingFlight]);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;

    if (type === "file") {
      const selectedFiles = Array.from(files);

      // Update form state with selected files
      setForm((prevForm) => ({
        ...prevForm,
        images: selectedFiles,
      }));

      // Generate image preview URLs
      const previewUrls = selectedFiles.map((file) =>
        URL.createObjectURL(file)
      );
      setPreviewImages(previewUrls);
      setSelectedImages(selectedFiles); // Track files for submission
    } else {
      setForm((prevForm) => ({
        ...prevForm,
        [name]: type === "checkbox" ? checked : value,
      }));
    }
  };

  // const handleChange = (e) => {
  //   const { name, value, type, checked, files } = e.target;

  //   if (type === "file") {
  //     console.log("Selected files:", e.target.files.length);
  //     const selectedFiles = Array.from(files);
  //     setForm({ ...form, images: selectedFiles });
  //     setPreviewImages(selectedFiles.map((file) => URL.createObjectURL(file)));
  //   } else {
  //     setForm({
  //       ...form,
  //       [name]: type === "checkbox" ? checked : value,
  //     });
  //   }
  // };

  const removeImage = (index) => {
    const updatedPreviews = [...previewImages];
    const updatedFiles = [...selectedImages];

    updatedPreviews.splice(index, 1);
    updatedFiles.splice(index, 1);

    setPreviewImages(updatedPreviews);
    setSelectedImages(updatedFiles);
    setForm((prevForm) => ({
      ...prevForm,
      images: updatedFiles,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Form images length:", form.images.length);
    const formData = new FormData();
    formData.append("name", form.name);
    formData.append("capacity", form.capacity);
    formData.append("description", form.description);
    formData.append("maxSpeed", form.maxSpeed);
    formData.append("status", form.status);

    form.images.forEach((img) => formData.append("images", img)); // append each image

    try {
      if (editingFlight) {
        await updateFlightType({
          id: editingFlight._id,
          formData,
        }).unwrap();
        toast.success("Flight type updated");
      } else {
        await createFlightType(formData).unwrap();
        toast.success("Flight type created");
      }

      setShowForm(false);
      refetch();
    } catch (err) {
      toast.error("Failed to save flight type");
    }
  };

  return (
    <form
      className="border p-4 mb-4"
      onSubmit={handleSubmit}
      encType="multipart/form-data"
    >
      <h5>{editingFlight ? "Edit" : "Add"} Flight Type</h5>
      <div className="row">
        <div className="col-md-4 mb-3">
          <input
            type="text"
            name="name"
            placeholder="Flight Name"
            className="form-control"
            value={form.name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="col-md-2 mb-3">
          <input
            type="number"
            name="capacity"
            placeholder="Capacity"
            className="form-control"
            value={form.capacity}
            onChange={handleChange}
            required
          />
        </div>
        <div className="col-md-3 mb-3">
          <input
            type="text"
            name="maxSpeed"
            placeholder="Max Speed"
            className="form-control"
            value={form.maxSpeed}
            onChange={handleChange}
            required
          />
        </div>
        <div className="col-md-3 mb-3">
          <input
            type="text"
            name="description"
            placeholder="Description"
            className="form-control"
            value={form.description}
            onChange={handleChange}
            required
          />
        </div>

        <div className="col-md-6 mb-3">
          <input
            type="file"
            name="images"
            className="form-control"
            accept="image/*"
            onChange={handleChange}
            multiple
          />
        </div>

        {previewImages.length > 0 &&
          previewImages.map((src, idx) => (
            <div className="col-md-2 mb-3 position-relative" key={idx}>
              <img
                src={src}
                alt={`Preview ${idx}`}
                width="100"
                className="rounded"
              />
              <button
                type="button"
                className="btn btn-sm btn-danger position-absolute top-0 end-0 translate-middle"
                style={{ borderRadius: "50%" }}
                onClick={() => removeImage(idx)}
              >
                <i className="bi bi-x"></i>
              </button>
            </div>
          ))}

        {/* {previewImages.length > 0 &&
          previewImages.map((src, idx) => (
            <div className="col-md-2 mb-3" key={idx}>
              <img
                src={src}
                alt={`Preview ${idx}`}
                width="100"
                className="rounded"
              />
            </div>
          ))} */}

        <div className="col-md-3 mb-3 d-flex align-items-center">
          <label className="form-check-label me-2" htmlFor="status">
            Active:
          </label>
          <input
            type="checkbox"
            id="status"
            name="status"
            checked={form.status}
            onChange={handleChange}
          />
        </div>
      </div>
      <button className="btn btn-success">
        {editingFlight ? "Update" : "Save"}
      </button>{" "}
      <button
        className="btn btn-secondary ms-2"
        onClick={() => setShowForm(false)}
      >
        Cancel
      </button>
    </form>
  );
};

export default FlightTypeForm;
