import React, { useEffect, useState } from "react";
import toast from "react-hot-toast";
import {
  useAddUserMutation,
  useUpdateUserMutation,
} from "../../redux/api/adminApi";

const AddUserForm = ({ onClose, refetch, user }) => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    role: "user",
  });

  const [addUser, { isLoading: adding }] = useAddUserMutation();
  const [updateUser, { isLoading: updating }] = useUpdateUserMutation();

  useEffect(() => {
    if (user) {
      setFormData({
        name: user.name || "",
        email: user.email || "",
        role: user.role || "user",
      });
    } else {
      setFormData({ name: "", email: "", role: "user" });
    }
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (user?._id) {
        await updateUser({ id: user._id, ...formData }).unwrap();
        toast.success("User updated successfully");
      } else {
        await addUser(formData).unwrap();
        toast.success("User added successfully");
      }

      onClose();
      refetch();
    } catch (error) {
      toast.error(error?.data?.message || "Operation failed");
    }
  };

  return (
    <form className="card p-3 mb-4" onSubmit={handleSubmit}>
      <h5>{user ? "Edit User" : "Add New User"}</h5>

      <div className="mb-2">
        <label className="form-label">Name</label>
        <input
          name="name"
          type="text"
          className="form-control"
          value={formData.name}
          onChange={handleChange}
          required
        />
      </div>

      <div className="mb-2">
        <label className="form-label">Email</label>
        <input
          name="email"
          type="email"
          className="form-control"
          value={formData.email}
          onChange={handleChange}
          required
          disabled={!!user} // Disable editing email when updating
        />
      </div>

      <div className="mb-2">
        <label className="form-label">Role</label>
        <select
          name="role"
          className="form-select"
          value={formData.role}
          onChange={handleChange}
        >
          <option value="user">User</option>
          <option value="admin">Admin</option>
        </select>
      </div>

      <div className="d-flex gap-2">
        <button
          type="submit"
          className="btn btn-success"
          disabled={adding || updating}
        >
          {user
            ? updating
              ? "Updating..."
              : "Update User"
            : adding
            ? "Adding..."
            : "Add User"}
        </button>
        <button type="button" className="btn btn-secondary" onClick={onClose}>
          Cancel
        </button>
      </div>
    </form>
  );
};

export default AddUserForm;
