import React, { useState, useMemo } from "react";
import {
  useGetFlightCategoriesQuery,
  useDeleteFlightCategoryMutation,
} from "../../redux/api/flightCategoryApi";
import FlightCategoryForm from "./FlightCategoryForm";
import DataTable from "react-data-table-component";
import toast from "react-hot-toast";

const FlightCategoryList = () => {
  const {
    data: categories,
    isLoading,
    refetch,
  } = useGetFlightCategoriesQuery();
  const [deleteFlightCategory] = useDeleteFlightCategoryMutation();
  const [editingCategory, setEditingCategory] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [searchText, setSearchText] = useState("");

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this category?")) {
      try {
        await deleteFlightCategory(id).unwrap();
        toast.success("Category deleted");
        refetch();
      } catch (err) {
        toast.error("Delete failed");
      }
    }
  };

  const handleEdit = (category) => {
    setEditingCategory(category);
    setShowForm(true);
  };

  const columns = [
    {
      name: "Name",
      selector: (row) => row.name,
      sortable: true,
    },
    {
      name: "Description",
      cell: (row) => (
        <div
          dangerouslySetInnerHTML={{
            __html:
              row.description?.length > 100
                ? row.description.substring(0, 100) + "..."
                : row.description,
          }}
        />
      ),
    },
    {
      name: "Image",
      cell: (row) =>
        row.image?.url ? (
          <img
            src={row.image.url}
            alt="Category"
            width="50"
            height="50"
            style={{ objectFit: "cover", borderRadius: "4px" }}
          />
        ) : (
          "-"
        ),
    },
    {
      name: "Actions",
      cell: (row) => (
        <div className="dropdown">
          <button
            className="btn btn-sm btn-secondary dropdown-toggle"
            type="button"
            data-bs-toggle="dropdown"
            aria-expanded="false"
          >
            Actions
          </button>
          <ul className="dropdown-menu">
            <li>
              <button className="dropdown-item" onClick={() => handleEdit(row)}>
                Edit
              </button>
            </li>
            <li>
              <button
                className="dropdown-item text-danger"
                onClick={() => handleDelete(row._id)}
              >
                Delete
              </button>
            </li>
          </ul>
        </div>
      ),
    },
  ];

  // Search filter
  const filteredData = useMemo(() => {
    if (!categories?.flightCategories) return [];
    return categories.flightCategories.filter((cat) =>
      cat.name.toLowerCase().includes(searchText.toLowerCase())
    );
  }, [categories, searchText]);

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h4>Flight Category List</h4>
        <button
          className="btn btn-primary"
          onClick={() => {
            setShowForm(!showForm);
            setEditingCategory(null);
          }}
        >
          {showForm ? "Hide Form" : "Add Flight Category"}
        </button>
      </div>

      {showForm && (
        <FlightCategoryForm
          editingCategory={editingCategory}
          setShowForm={setShowForm}
          refetch={refetch}
        />
      )}

      <div className="mb-3">
        <input
          type="text"
          placeholder="Search by name"
          className="form-control"
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
        />
      </div>

      <DataTable
        columns={columns}
        data={filteredData}
        pagination
        progressPending={isLoading}
        highlightOnHover
        responsive
        striped
        persistTableHead
      />
    </div>
  );
};

export default FlightCategoryList;
