import React, { useEffect, useState, useRef } from "react";
import {
  useCreateBookingMutation,
  useUpdateBookingMutation,
} from "../../redux/api/bookingApi";
import { useGetUsersQuery } from "../../redux/api/adminApi";
import { useGetFlightTypesQuery } from "../../redux/api/flightTypeApi";
import { useGetAirportsQuery } from "../../redux/api/airportApi"; // ✅ Airport API
import Select from "react-select";
import toast from "react-hot-toast";

const AddBookingForm = ({ setShowForm, refetch, booking }) => {
  const formRef = useRef(null);
  const [form, setForm] = useState({
    user: "",
    fromPlace: "",
    toPlace: "",
    travelDate: "",
    travelTime: "",
    flightType: "",
    pax: "",
    status: "pending",
    remarks: "",
    totalAmount: "",
    bookingType: "normal",
  });

  useEffect(() => {
    if (booking && formRef.current) {
      formRef.current.scrollIntoView({ behavior: "smooth", block: "start" });
    }
  }, [booking]);

  useEffect(() => {
    if (booking) {
      setForm({
        user: booking.user?._id || "",
        fromPlace: booking.fromPlace?._id || booking.fromPlace || "",
        toPlace: booking.toPlace?._id || booking.toPlace || "",
        travelDate: booking.travelDate || "",
        travelTime: booking.travelTime || "",
        flightType: booking.flightType?._id || booking.flightType || "",
        pax: booking.pax || "",
        status: booking.status || "pending",
        remarks: booking.remarks || "",
        totalAmount: booking.totalAmount || 0,
        bookingType: booking.bookingType || "normal",
      });
    }
  }, [booking]);

  const { data: users } = useGetUsersQuery();
  const { data: flightTypes } = useGetFlightTypesQuery();
  const { data: airports } = useGetAirportsQuery(); // ✅ get airports
  const [createBooking] = useCreateBookingMutation();
  const [updateBooking] = useUpdateBookingMutation();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (
      !form.user ||
      !form.fromPlace ||
      !form.toPlace ||
      !form.travelDate ||
      !form.travelTime ||
      !form.flightType ||
      !form.totalAmount ||
      !form.pax
    ) {
      toast.error("Please fill all required fields");
      return;
    }

    try {
      const payload = {
        ...form,
        user: form.user?._id || form.user,
        fromPlace: form.fromPlace?._id || form.fromPlace,
        toPlace: form.toPlace?._id || form.toPlace,
        flightType: form.flightType?._id || form.flightType,
        totalAmount: parseFloat(form.totalAmount),
      };

      if (booking?._id) {
        await updateBooking({ id: booking._id, data: payload });
      } else {
        await createBooking(payload);
      }

      toast.success(`Booking ${booking ? "updated" : "created"} successfully`);
      refetch();
      setShowForm(false);
    } catch (err) {
      console.error("Error submitting form", err);
      toast.error("Failed to create booking");
    }
  };

  // Dropdown options
  const userOptions = users?.users?.map((u) => ({
    value: u._id,
    label: `${u.name} (${u.email})`,
  }));

  const flightOptions = flightTypes?.flightTypes?.map((f) => ({
    value: f._id,
    label: `${f.name} (${f.capacity} seats)`,
  }));

  const airportOptions = airports?.airports?.map((a) => ({
    value: a._id,
    label: `${a.name} (${a.code}) - ${a.city}`,
  }));

  return (
    <form
      ref={formRef}
      onSubmit={handleSubmit}
      className="border p-3 mb-4 bg-light"
    >
      <div className="row g-3">
        {/* Traveler */}
        <div className="col-md-4">
          <Select
            options={userOptions}
            value={userOptions?.find((o) => o.value === form.user) || null}
            onChange={(selected) =>
              setForm((prev) => ({ ...prev, user: selected?.value || "" }))
            }
            placeholder="Select Traveler"
            isClearable
            isSearchable
            required
          />
        </div>

        {/* From Place */}
        <div className="col-md-4">
          <Select
            options={airportOptions}
            value={
              airportOptions?.find((o) => o.value === form.fromPlace) || null
            }
            onChange={(selected) =>
              setForm((prev) => ({ ...prev, fromPlace: selected?.value || "" }))
            }
            placeholder="Select From Airport"
            isClearable
            isSearchable
            required
          />
        </div>

        {/* To Place */}
        <div className="col-md-4">
          <Select
            options={airportOptions}
            value={
              airportOptions?.find((o) => o.value === form.toPlace) || null
            }
            onChange={(selected) =>
              setForm((prev) => ({ ...prev, toPlace: selected?.value || "" }))
            }
            placeholder="Select To Airport"
            isClearable
            isSearchable
            required
          />
        </div>

        {/* Date */}
        <div className="col-md-4">
          <input
            type="date"
            className="form-control"
            value={form.travelDate}
            onChange={(e) => setForm({ ...form, travelDate: e.target.value })}
            required
          />
        </div>

        {/* Time */}
        <div className="col-md-4">
          <input
            type="time"
            className="form-control"
            value={form.travelTime}
            onChange={(e) => setForm({ ...form, travelTime: e.target.value })}
            required
          />
        </div>

        {/* Pax */}
        <div className="col-md-4">
          <input
            type="number"
            className="form-control"
            placeholder="Number of Passengers"
            value={form.pax}
            onChange={(e) => setForm({ ...form, pax: e.target.value })}
            required
          />
        </div>

        {/* Flight */}
        <div className="col-md-4">
          <Select
            options={flightOptions}
            value={
              flightOptions?.find((o) => o.value === form.flightType) || null
            }
            onChange={(selected) =>
              setForm((prev) => ({
                ...prev,
                flightType: selected?.value || "",
              }))
            }
            placeholder="Select Flight Type"
            isClearable
            isSearchable
            required
          />
        </div>

        {/* Amount */}
        <div className="col-md-4">
          <input
            type="number"
            className="form-control"
            placeholder="Total Amount"
            value={form.totalAmount}
            onChange={(e) => setForm({ ...form, totalAmount: e.target.value })}
            required
          />
        </div>

        {/* Remarks */}
        <div className="col-md-8">
          <textarea
            className="form-control"
            rows="2"
            placeholder="Remarks"
            value={form.remarks}
            onChange={(e) => setForm({ ...form, remarks: e.target.value })}
          />
        </div>

        {/* Booking Type */}
        <div className="col-md-6">
          <label>Booking Type</label>
          <select
            className="form-control"
            value={form.bookingType}
            onChange={(e) => setForm({ ...form, bookingType: e.target.value })}
            required
          >
            <option value="normal">Normal Booking</option>
            <option value="empty_legs">Empty Legs</option>
          </select>
        </div>

        {/* Status */}
        <div className="col-md-6">
          <label>Status</label>
          <select
            className="form-control"
            value={form.status}
            onChange={(e) => setForm({ ...form, status: e.target.value })}
            required
          >
            <option value="pending">Pending</option>
            <option value="confirmed_payment_pending">
              Confirmed (Payment Pending)
            </option>
            <option value="confirmed">Confirmed</option>
            <option value="cancelled">Cancelled</option>
          </select>
        </div>

        {/* Buttons */}
        <div className="col-md-12 d-flex gap-2 mt-2">
          <button type="submit" className="btn btn-success">
            Submit Booking
          </button>
          <button
            type="button"
            className="btn btn-secondary"
            onClick={() => setShowForm(false)}
          >
            Cancel
          </button>
        </div>
      </div>
    </form>
  );
};

export default AddBookingForm;
