// src/components/models/AddModelForm.jsx
import React, { useEffect, useRef, useState } from "react";
import toast from "react-hot-toast";
import { useLazyGetUploadSignatureQuery } from "../../redux/api/cloudinaryApi";
import {
  useCreateModelMutation,
  useUpdateModelMutation,
  useSaveProfileMutation,
  useSavePortfolioMutation,
  useDeletePortfolioImageMutation,
} from "../../redux/api/modelApi";
import {
  feetInchToCm,
  lbToKg,
  calculateAgeFromDOB,
  cmToFeetInch,
  kgToLb,
} from "../../utils/convert";
import { getCloudinarySignature } from "../../../../backend/controllers/cloudinaryController";

const AddModelForm = ({ model, onSaved }) => {
  const ref = useRef(null);

  // form state matches schema names
  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    stageName: "",
    gender: "Female",
    dateOfBirth: "",
    email: "",
    phone: "",
    country: "",
    state: "",
    city: "",
    // measurements stored metric
    heightCm: "",
    weightKg: "",
    bustCm: "",
    waistCm: "",
    hipsCm: "",
    chestCm: "",
    shoeSize: "",
    eyeColor: "",
    hairColor: "",
    skinTone: "",
    bodyType: "Average",
    tattoos: false,
    piercings: false,
    experienceLevel: "Beginner",
    languages: "",
    skills: "",
    availableFor: "",
    travelWillingness: "Yes",
    expectedPayPerDay: "",
    status: "pending",
    featured: false,
  });

  const [localPortfolio, setLocalPortfolio] = useState([]); // existing saved portfolio
  const [profileFile, setProfileFile] = useState(null);
  const [portfolioFiles, setPortfolioFiles] = useState([]);

  // helpers
  const [getSignature] = useLazyGetUploadSignatureQuery();
  const [createModel, { isLoading: creating }] = useCreateModelMutation();
  const [updateModel, { isLoading: updating }] = useUpdateModelMutation();
  const [saveProfile] = useSaveProfileMutation();
  const [savePortfolio] = useSavePortfolioMutation();
  const [deletePortfolioImage] = useDeletePortfolioImageMutation();

  useEffect(() => {
    if (model) {
      setForm({
        firstName: model.firstName || "",
        lastName: model.lastName || "",
        stageName: model.stageName || "",
        gender: model.gender || "Female",
        dateOfBirth: model.dateOfBirth ? model.dateOfBirth.slice(0, 10) : "",
        email: model.email || "",
        phone: model.phone || "",
        country: model.country || "",
        state: model.state || "",
        city: model.city || "",
        heightCm: model.heightCm || "",
        weightKg: model.weightKg || "",
        bustCm: model.bustCm || "",
        waistCm: model.waistCm || "",
        hipsCm: model.hipsCm || "",
        chestCm: model.chestCm || "",
        shoeSize: model.shoeSize || "",
        eyeColor: model.eyeColor || "",
        hairColor: model.hairColor || "",
        skinTone: model.skinTone || "",
        bodyType: model.bodyType || "Average",
        tattoos: model.tattoos || false,
        piercings: model.piercings || false,
        experienceLevel: model.experienceLevel || "Beginner",
        languages: model.languages ? model.languages.join(",") : "",
        skills: model.skills ? model.skills.join(",") : "",
        availableFor: model.availableFor ? model.availableFor.join(",") : "",
        travelWillingness: model.travelWillingness || "Yes",
        expectedPayPerDay: model.expectedPayPerDay || "",
        status: model.status || "pending",
        featured: model.featured || false,
      });
      setLocalPortfolio(model.portfolio || []);
      setTimeout(() => ref.current?.scrollIntoView({ behavior: "smooth" }), 80);
    } else {
      // reset when creating new
      setLocalPortfolio([]);
    }
  }, [model]);

  const isLoading = creating || updating;

  // Upload helper: signed upload to Cloudinary
  const uploadToCloudinary = async (file, folder) => {
    const { data: sig } = await getCloudinarySignature(folder).unwrap();
    const formData = new FormData();
    formData.append("file", file);
    formData.append("api_key", sig.apiKey);
    formData.append("timestamp", sig.timestamp);
    formData.append("signature", sig.signature);
    formData.append("folder", sig.folder);
    const res = await fetch(
      `https://api.cloudinary.com/v1_1/${sig.cloudName}/auto/upload`,
      { method: "POST", body: formData }
    );
    return await res.json();
  };

  const handleDeletePortfolio = async (public_id) => {
    if (!window.confirm("Delete this image?")) return;
    try {
      await deletePortfolioImage({ id: model._id, public_id }).unwrap();
      setLocalPortfolio((p) => p.filter((x) => x.public_id !== public_id));
      toast.success("Deleted");
    } catch (err) {
      toast.error("Delete failed");
    }
  };

  // convert input either ft/in OR cm, and lb or kg
  // UI will allow both (two small inputs for ft/in and one for cm for convenience)
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // prepare payload
      const payload = { ...form };

      // convert skills, languages, availableFor arrays
      payload.skills = form.skills
        ? form.skills.split(",").map((s) => s.trim())
        : [];
      payload.languages = form.languages
        ? form.languages.split(",").map((s) => s.trim())
        : [];
      payload.availableFor = form.availableFor
        ? form.availableFor.split(",").map((s) => s.trim())
        : [];

      // make sure heightCm & weightKg are numbers (if empty, allow null)
      payload.heightCm = payload.heightCm
        ? Number(payload.heightCm)
        : undefined;
      payload.weightKg = payload.weightKg
        ? Number(payload.weightKg)
        : undefined;

      let res;
      if (model?._id) {
        res = await updateModel({ id: model._id, data: payload }).unwrap();
      } else {
        res = await createModel(payload).unwrap();
      }

      const modelId = model?._id || res.model?._id || res._id;

      // profile upload (signed)
      if (profileFile) {
        const uploaded = await uploadToCloudinary(
          profileFile,
          "Aayo/models/profile"
        );
        await saveProfile({
          id: modelId,
          url: uploaded.secure_url,
          public_id: uploaded.public_id,
          type: uploaded.resource_type,
        }).unwrap();
      }

      // portfolio upload (signed multiple)
      if (portfolioFiles && portfolioFiles.length > 0) {
        const uploads = [];
        for (const f of portfolioFiles) {
          const up = await uploadToCloudinary(f, "Aayo/models/portfolio");
          uploads.push({
            url: up.secure_url,
            public_id: up.public_id,
            type: up.resource_type,
          });
        }
        await savePortfolio({ id: modelId, images: uploads }).unwrap();
      }

      toast.success(`Model ${model ? "updated" : "created"} successfully`);
      if (onSaved) onSaved();
    } catch (err) {
      console.error(err);
      toast.error(err?.data?.message || err?.message || "Save failed");
    }
  };

  // Auto-calc age from DOB
  const age = form.dateOfBirth ? calculateAgeFromDOB(form.dateOfBirth) : null;

  return (
    <form
      ref={ref}
      onSubmit={handleSubmit}
      className="border p-3 mb-4 bg-light"
    >
      <div className="row g-3">
        {/* Basic */}
        <div className="col-md-4">
          <label>First name*</label>
          <input
            required
            value={form.firstName}
            onChange={(e) =>
              setForm((p) => ({ ...p, firstName: e.target.value }))
            }
            className="form-control"
          />
        </div>
        <div className="col-md-4">
          <label>Last name</label>
          <input
            value={form.lastName}
            onChange={(e) =>
              setForm((p) => ({ ...p, lastName: e.target.value }))
            }
            className="form-control"
          />
        </div>
        <div className="col-md-4">
          <label>Stage name</label>
          <input
            value={form.stageName}
            onChange={(e) =>
              setForm((p) => ({ ...p, stageName: e.target.value }))
            }
            className="form-control"
          />
        </div>

        <div className="col-md-3">
          <label>Gender</label>
          <select
            value={form.gender}
            onChange={(e) => setForm((p) => ({ ...p, gender: e.target.value }))}
            className="form-control"
          >
            <option>Female</option>
            <option>Male</option>
            <option>Non-binary</option>
            <option>Other</option>
          </select>
        </div>

        <div className="col-md-3">
          <label>Date of birth</label>
          <input
            type="date"
            value={form.dateOfBirth}
            onChange={(e) =>
              setForm((p) => ({ ...p, dateOfBirth: e.target.value }))
            }
            className="form-control"
          />
        </div>

        <div className="col-md-2">
          <label>Age</label>
          <input value={age ?? ""} readOnly className="form-control" />
        </div>

        <div className="col-md-4">
          <label>Email</label>
          <input
            value={form.email}
            onChange={(e) => setForm((p) => ({ ...p, email: e.target.value }))}
            className="form-control"
          />
        </div>

        <div className="col-md-4">
          <label>Phone</label>
          <input
            value={form.phone}
            onChange={(e) => setForm((p) => ({ ...p, phone: e.target.value }))}
            className="form-control"
          />
        </div>

        {/* Location */}
        <div className="col-md-3">
          <label>Country</label>
          <input
            value={form.country}
            onChange={(e) =>
              setForm((p) => ({ ...p, country: e.target.value }))
            }
            className="form-control"
          />
        </div>
        <div className="col-md-3">
          <label>State</label>
          <input
            value={form.state}
            onChange={(e) => setForm((p) => ({ ...p, state: e.target.value }))}
            className="form-control"
          />
        </div>
        <div className="col-md-3">
          <label>City</label>
          <input
            value={form.city}
            onChange={(e) => setForm((p) => ({ ...p, city: e.target.value }))}
            className="form-control"
          />
        </div>

        {/* Measurements: show cm/kg fields primarily, optionally allow conversions in UI */}
        <div className="col-md-3">
          <label>Height (cm)</label>
          <input
            type="number"
            value={form.heightCm}
            onChange={(e) =>
              setForm((p) => ({ ...p, heightCm: e.target.value }))
            }
            className="form-control"
          />
          <small className="text-muted">Or convert from ft/in below</small>
        </div>

        <div className="col-md-2">
          <label>Feet</label>
          <input
            type="number"
            min="0"
            step="1"
            className="form-control"
            id="ft"
            onChange={(e) => {
              // convert ft/in input to cm if inch exists
              const ft = Number(e.target.value || 0);
              const inchInput = document.getElementById("in")?.value || 0;
              const cm = feetInchToCm(ft, inchInput);
              setForm((p) => ({ ...p, heightCm: cm }));
            }}
          />
        </div>
        <div className="col-md-2">
          <label>Inches</label>
          <input
            type="number"
            min="0"
            step="1"
            className="form-control"
            id="in"
            onChange={(e) => {
              const inch = Number(e.target.value || 0);
              const ftInput = document.getElementById("ft")?.value || 0;
              const cm = feetInchToCm(ftInput, inch);
              setForm((p) => ({ ...p, heightCm: cm }));
            }}
          />
        </div>

        <div className="col-md-3">
          <label>Weight (kg)</label>
          <input
            type="number"
            value={form.weightKg}
            onChange={(e) =>
              setForm((p) => ({ ...p, weightKg: e.target.value }))
            }
            className="form-control"
          />
          <small className="text-muted">Or convert from lb below</small>
        </div>

        <div className="col-md-2">
          <label>Weight (lb)</label>
          <input
            type="number"
            className="form-control"
            id="lb"
            onChange={(e) => {
              const lb = Number(e.target.value || 0);
              const kg = lbToKg(lb);
              setForm((p) => ({ ...p, weightKg: kg }));
            }}
          />
        </div>

        {/* Other measurements */}
        <div className="col-md-2">
          <label>Bust (cm)</label>
          <input
            value={form.bustCm}
            onChange={(e) => setForm((p) => ({ ...p, bustCm: e.target.value }))}
            className="form-control"
          />
        </div>
        <div className="col-md-2">
          <label>Waist (cm)</label>
          <input
            value={form.waistCm}
            onChange={(e) =>
              setForm((p) => ({ ...p, waistCm: e.target.value }))
            }
            className="form-control"
          />
        </div>
        <div className="col-md-2">
          <label>Hips (cm)</label>
          <input
            value={form.hipsCm}
            onChange={(e) => setForm((p) => ({ ...p, hipsCm: e.target.value }))}
            className="form-control"
          />
        </div>
        <div className="col-md-2">
          <label>Chest (cm)</label>
          <input
            value={form.chestCm}
            onChange={(e) =>
              setForm((p) => ({ ...p, chestCm: e.target.value }))
            }
            className="form-control"
          />
        </div>

        <div className="col-md-3">
          <label>Shoe Size</label>
          <input
            value={form.shoeSize}
            onChange={(e) =>
              setForm((p) => ({ ...p, shoeSize: e.target.value }))
            }
            className="form-control"
          />
        </div>
        <div className="col-md-3">
          <label>Eye Color</label>
          <input
            value={form.eyeColor}
            onChange={(e) =>
              setForm((p) => ({ ...p, eyeColor: e.target.value }))
            }
            className="form-control"
          />
        </div>
        <div className="col-md-3">
          <label>Hair Color</label>
          <input
            value={form.hairColor}
            onChange={(e) =>
              setForm((p) => ({ ...p, hairColor: e.target.value }))
            }
            className="form-control"
          />
        </div>
        <div className="col-md-3">
          <label>Skin Tone</label>
          <input
            value={form.skinTone}
            onChange={(e) =>
              setForm((p) => ({ ...p, skinTone: e.target.value }))
            }
            className="form-control"
          />
        </div>

        <div className="col-md-3">
          <label>Body Type</label>
          <select
            className="form-control"
            value={form.bodyType}
            onChange={(e) =>
              setForm((p) => ({ ...p, bodyType: e.target.value }))
            }
          >
            <option>Slim</option>
            <option>Athletic</option>
            <option>Average</option>
            <option>Plus-size</option>
            <option>Fit</option>
          </select>
        </div>

        <div className="col-md-2 form-check">
          <label className="form-check-label">Tattoos</label>
          <br />
          <input
            type="checkbox"
            checked={form.tattoos}
            onChange={(e) =>
              setForm((p) => ({ ...p, tattoos: e.target.checked }))
            }
          />
        </div>
        <div className="col-md-2 form-check">
          <label className="form-check-label">Piercings</label>
          <br />
          <input
            type="checkbox"
            checked={form.piercings}
            onChange={(e) =>
              setForm((p) => ({ ...p, piercings: e.target.checked }))
            }
          />
        </div>

        {/* Experience */}
        <div className="col-md-4">
          <label>Experience Level</label>
          <select
            className="form-control"
            value={form.experienceLevel}
            onChange={(e) =>
              setForm((p) => ({ ...p, experienceLevel: e.target.value }))
            }
          >
            <option>Beginner</option>
            <option>Intermediate</option>
            <option>Professional</option>
          </select>
        </div>

        <div className="col-md-6">
          <label>Languages (comma separated)</label>
          <input
            value={form.languages}
            onChange={(e) =>
              setForm((p) => ({ ...p, languages: e.target.value }))
            }
            className="form-control"
          />
        </div>
        <div className="col-md-6">
          <label>Skills (comma separated)</label>
          <input
            value={form.skills}
            onChange={(e) => setForm((p) => ({ ...p, skills: e.target.value }))}
            className="form-control"
          />
        </div>

        <div className="col-md-6">
          <label>Available For (comma separated)</label>
          <input
            value={form.availableFor}
            onChange={(e) =>
              setForm((p) => ({ ...p, availableFor: e.target.value }))
            }
            className="form-control"
          />
        </div>
        <div className="col-md-3">
          <label>Travel Willingness</label>
          <select
            className="form-control"
            value={form.travelWillingness}
            onChange={(e) =>
              setForm((p) => ({ ...p, travelWillingness: e.target.value }))
            }
          >
            <option>Yes</option>
            <option>No</option>
            <option>Within city</option>
          </select>
        </div>
        <div className="col-md-3">
          <label>Expected Pay / day (number)</label>
          <input
            type="number"
            value={form.expectedPayPerDay}
            onChange={(e) =>
              setForm((p) => ({ ...p, expectedPayPerDay: e.target.value }))
            }
            className="form-control"
          />
        </div>

        {/* Media uploads */}
        <div className="col-md-4">
          <label>Profile Image</label>
          <input
            type="file"
            accept="image/*"
            onChange={(e) => setProfileFile(e.target.files[0])}
            className="form-control"
          />
        </div>

        {/* Existing portfolio */}
        {localPortfolio && localPortfolio.length > 0 && (
          <div className="col-md-12">
            <label className="fw-bold">Existing Portfolio</label>
            <div className="d-flex flex-wrap gap-3 mt-2">
              {localPortfolio.map((img) => (
                <div
                  key={img.public_id}
                  style={{
                    position: "relative",
                    width: 120,
                    height: 120,
                    border: "1px solid #ddd",
                    borderRadius: 6,
                    overflow: "hidden",
                  }}
                >
                  <img
                    src={img.url}
                    alt=""
                    style={{
                      width: "100%",
                      height: "100%",
                      objectFit: "cover",
                    }}
                  />
                  <button
                    type="button"
                    className="btn btn-danger btn-sm"
                    style={{ position: "absolute", top: 6, right: 6 }}
                    onClick={() => handleDeletePortfolio(img.public_id)}
                  >

                  </button>
                </div>
              ))}
            </div>
          </div>
        )}

        <div className="col-md-8">
          <label>Portfolio Images/Videos (multiple)</label>
          <input
            type="file"
            accept="image/*,video/*"
            multiple
            onChange={(e) => setPortfolioFiles(Array.from(e.target.files))}
            className="form-control"
          />
        </div>

        {/* Admin */}
        <div className="col-md-3">
          <label>Status</label>
          <select
            className="form-control"
            value={form.status}
            onChange={(e) => setForm((p) => ({ ...p, status: e.target.value }))}
          >
            <option>pending</option>
            <option>approved</option>
            <option>rejected</option>
            <option>inactive</option>
          </select>
        </div>
        <div className="col-md-2">
          <label>Featured</label>
          <br />
          <input
            type="checkbox"
            checked={form.featured}
            onChange={(e) =>
              setForm((p) => ({ ...p, featured: e.target.checked }))
            }
          />
        </div>

        <div className="col-md-12 d-flex gap-2 mt-3">
          <button
            type="submit"
            className="btn btn-success"
            disabled={isLoading}
          >
            {isLoading
              ? "Processing..."
              : model
              ? "Update Model"
              : "Create Model"}
          </button>
          <button
            type="button"
            className="btn btn-secondary"
            onClick={() => onSaved && onSaved()}
          >
            Cancel
          </button>
        </div>
      </div>
    </form>
  );
};

export default AddModelForm;
