import React, { useState, useEffect } from "react";
import {
  useGetEmptyLegsQuery,
  useDeleteEmptyLegMutation,
  useToggleEmptyLegStatusMutation,
} from "../../redux/api/emptyLegApi";

import EmptyLegForm from "./EmptyLegForm";
import DataTable from "react-data-table-component";
import toast from "react-hot-toast";

const EmptyLegList = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredLegs, setFilteredLegs] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingLeg, setEditingLeg] = useState(null);

  const { data, isLoading } = useGetEmptyLegsQuery();
  const [deleteLeg] = useDeleteEmptyLegMutation();
  const [toggleStatus] = useToggleEmptyLegStatusMutation();

  useEffect(() => {
    if (data?.emptyLegs) {
      const filtered = data.emptyLegs.filter((item) => {
        const s = searchTerm.toLowerCase();
        return (
          item.from?.toLowerCase().includes(s) ||
          item.to?.toLowerCase().includes(s) ||
          item.flightType?.name?.toLowerCase().includes(s)
        );
      });
      setFilteredLegs(filtered);
    }
  }, [data, searchTerm]);

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this empty leg?")) return;
    try {
      await deleteLeg(id).unwrap();
      toast.success("Deleted successfully");
    } catch {
      toast.error("Delete failed");
    }
  };

  const handleToggle = async (id) => {
    await toggleStatus(id);
    toast.success("Status updated");
  };

  const columns = [
    {
      name: "Flight Type",
      selector: (row) => row.flightType?.name || "",
      sortable: true,
    },
    {
      name: "From - To",
      selector: (row) => `${row.from} → ${row.to}`,
    },
    {
      name: "Date",
      selector: (row) => row.date,
      sortable: true,
    },
    {
      name: "Time",
      selector: (row) => row.time,
    },
    {
      name: "Exact Price",
      selector: (row) => `₹${row.exactPrice}`,
    },
    {
      name: "Offer Price",
      selector: (row) => (row.offerPrice ? `₹${row.offerPrice}` : "—"),
    },
    {
      name: "Status",
      selector: (row) => (
        <span
          className={`badge ${
            row.status === "published" ? "bg-success" : "bg-warning"
          }`}
        >
          {row.status}
        </span>
      ),
    },
    {
      name: "Actions",
      cell: (row) => (
        <div className="d-flex gap-2">
          <button
            className="btn btn-sm btn-primary"
            onClick={() => {
              setEditingLeg(row);
              setShowForm(true);
            }}
          >
            Edit
          </button>

          <button
            className="btn btn-sm btn-danger"
            onClick={() => handleDelete(row._id)}
          >
            Delete
          </button>

          <button
            className="btn btn-sm btn-secondary"
            onClick={() => handleToggle(row._id)}
          >
            Toggle Status
          </button>
        </div>
      ),
    },
  ];

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between mb-3">
        <h4>Empty Legs</h4>
        <button
          className="btn btn-primary"
          onClick={() => {
            setEditingLeg(null);
            setShowForm(!showForm);
          }}
        >
          {showForm ? "Close Form" : "Add Empty Leg"}
        </button>
      </div>

      {showForm && (
        <EmptyLegForm
          editingLeg={editingLeg}
          onSuccess={() => setShowForm(false)}
        />
      )}

      <input
        type="text"
        className="form-control mb-3"
        placeholder="Search empty legs..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <DataTable
        columns={columns}
        data={filteredLegs}
        progressPending={isLoading}
        pagination
        highlightOnHover
      />
    </div>
  );
};

export default EmptyLegList;
