import React, { useState } from "react";

export default function EmptyLegModal({ legs, onClose }) {
  const [updated, setUpdated] = useState(legs);

  const handlePriceChange = (index, value) => {
    const copy = [...updated];
    copy[index].offerPrice = value;
    setUpdated(copy);
  };

  const handlePublish = async (leg) => {
    await fetch(`/api/v1/empty-legs/${leg._id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ offerPrice: leg.offerPrice, status: "published" }),
    });

    alert("Empty Leg Published!");
  };

  return (
    <div className="modal-overlay">
      <div className="modal-container">
        <h3>Generated Empty Legs</h3>

        {updated.map((leg, index) => (
          <div key={leg._id} className="mb-3 border p-3">
            <h5>
              {leg.from} → {leg.to}
            </h5>
            <p>
              Date: {leg.date} | Time: {leg.time}
            </p>
            <p>
              <b>Exact Price:</b> ₹{leg.exactPrice}
            </p>

            <label>Offer Price</label>
            <input
              type="number"
              className="form-control"
              value={leg.offerPrice || ""}
              onChange={(e) => handlePriceChange(index, e.target.value)}
            />

            <button
              className="btn btn-success mt-2"
              onClick={() => handlePublish(leg)}
            >
              Publish
            </button>
          </div>
        ))}

        <button className="btn btn-secondary" onClick={onClose}>
          Close
        </button>
      </div>
    </div>
  );
}
