import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";
import Footer from "./components/layout/Footer";
import Header from "./components/layout/Header";
import Sidebar from "./components/layout/Sidebar";
import useAdminRoutes from "./components/routes/adminRoutes";
// import useUserRoutes from "./components/routes/userRoutes";
import { Toaster } from "react-hot-toast";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import ForgotPassword from "./components/auth/ForgotPassword";

function Layout() {
  const location = useLocation();
  const adminRoutes = useAdminRoutes();
  // const userRoutes = useUserRoutes();

  const isLoginOrRegisterPage = ["/admin/login", "/register", "/password/forgot"].includes(location.pathname);


  return (
    <div className="App">
      <Toaster position="top-center" />
      {isLoginOrRegisterPage ? (
        <Routes>
          <Route path="/admin/login" element={<Login position="center center" />} />
          <Route path="/register" element={<Register />} />
          <Route path="/password/forgot" element={<ForgotPassword position="center center" />} />
        </Routes>
      ) : (
        <div className="wrapper">
          <Sidebar />
          <div className="main-panel">
            <Header />
            <div className="container">
              <Routes>
                {adminRoutes}
                {/* {userRoutes} */}
              </Routes>
            </div>
            <Footer />
          </div>
        </div>
      )}
    </div>
  );
}

function App() {
  return (
    <Router>
      <Layout />
    </Router>
  );
}

export default App;
