import React, { useState } from "react";
import {
  useGetAirportsQuery,
  useDeleteAirportMutation,
} from "../../redux/api/airportApi";
import AirportForm from "./AirportForm";
import toast from "react-hot-toast";
const AirportList = () => {
  const { data, isLoading } = useGetAirportsQuery();
  const [deleteAirport] = useDeleteAirportMutation();
  const [editingAirport, setEditingAirport] = useState(null);

  const handleDelete = async (id) => {
    await deleteAirport(id);
    toast.success("Airport deleted");
  };

  return (
    <div className="container mt-4">
      <h4>Airport List</h4>
      <AirportForm
        editingAirport={editingAirport}
        onSuccess={() => setEditingAirport(null)}
      />
      <table className="table mt-4">
        <thead>
          <tr>
            <th>Name</th>
            <th>Code</th>
            <th>City</th>
            <th>Country</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {data?.airports?.map((a) => (
            <tr key={a._id}>
              <td>{a.name}</td>
              <td>{a.code}</td>
              <td>{a.city}</td>
              <td>{a.country}</td>
              <td>
                <button
                  className="btn btn-primary me-2"
                  onClick={() => setEditingAirport(a)}
                >
                  Edit
                </button>
                <button
                  className="btn btn-danger"
                  onClick={() => handleDelete(a._id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
export default AirportList;
