// src/redux/slices/authSlice.js

import { createSlice } from '@reduxjs/toolkit';
import { authApi } from '../api/authApi'; // ← THIS IMPORT MUST BE CORRECT!

const authSlice = createSlice({
    name: 'auth',
    initialState: {
        user: null,
        token: null,
        isAuthenticated: false,
    },
    reducers: {
        logout: (state) => {
            state.user = null;
            state.token = null;
            state.isAuthenticated = false;
            localStorage.removeItem('authToken');
        },
    },
    extraReducers: (builder) => {
        builder
            // THIS IS THE CRITICAL PART – updates state when login succeeds
            .addMatcher(
                authApi.endpoints.login.matchFulfilled,
                (state, action) => {
                    const payload = action.payload;
                    console.log('Login successful! Payload:', payload); // You should see this in console

                    state.token = payload.token;
                    state.user = payload.user;
                    state.isAuthenticated = true;

                    // Save token so it survives page refresh
                    localStorage.setItem('authToken', payload.token);
                }
            )
            // Optional: clear on error
            .addMatcher(authApi.endpoints.login.matchRejected, (state) => {
                state.token = null;
                state.user = null;
                state.isAuthenticated = false;
            });
    },
});

export const { logout } = authSlice.actions;
export default authSlice.reducer;
