import React, { useState, useEffect } from "react";

export default function EmptyLegModal({ legs, onClose }) {
  const [updated, setUpdated] = useState(
    legs.map((l) => ({
      ...l,
      isCompleted: Boolean(l.offerPrice), // detect pre-filled offer price
    }))
  );

  useEffect(() => {
    document.body.style.overflow = "hidden";
    return () => (document.body.style.overflow = "auto");
  }, []);

  //=========== HANDLE PRICE CHANGE ===========
  const handlePriceChange = (index, value) => {
    const copy = [...updated];
    copy[index].offerPrice = value;
    setUpdated(copy);
  };

  const handlePublish = async (leg, index) => {
    await fetch(`/api/v1/admin/empty-legs/${leg._id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        offerPrice: leg.offerPrice,
        status: "published",
      }),
    });

    const copy = [...updated];
    copy[index].isCompleted = true; // ✔ Only mark after publish
    setUpdated(copy);

    alert("Empty Leg Published!");

    // Auto close if all completed
    if (copy.every((l) => l.isCompleted)) {
      setTimeout(() => onClose(), 400);
    }
  };

  return (
    <div
      className="modal fade show"
      style={{
        display: "block",
        background: "rgba(0,0,0,0.6)",
        zIndex: 99999,
      }}
    >
      <div className="modal-dialog modal-lg modal-dialog-centered">
        <div className="modal-content">
          {/* Header */}
          <div className="modal-header">
            <h5 className="modal-title">Generated Empty Legs</h5>
            <button className="btn-close" onClick={onClose}></button>
          </div>

          {/* Body */}
          <div className="modal-body">
            {updated.map((leg, index) => (
              <div key={leg._id} className="border rounded p-3 mb-3">
                <h5 className="mb-2">
                  {leg.from} → {leg.to}
                </h5>

                <p className="mb-1">
                  <strong>Date:</strong> {leg.date} | <strong>Time:</strong>{" "}
                  {leg.time}
                </p>

                <p className="mb-2">
                  <strong>Exact Price:</strong> ₹{leg.exactPrice}
                </p>

                {/* If offer price is already entered (or after entering), show static value */}
                {leg.isCompleted ? (
                  <p>
                    <strong>Offer Price:</strong> ₹{leg.offerPrice}
                  </p>
                ) : (
                  <>
                    <label className="form-label">Offer Price</label>
                    <input
                      type="number"
                      className="form-control"
                      value={leg.offerPrice || ""}
                      onChange={(e) => handlePriceChange(index, e.target.value)}
                    />
                  </>
                )}

                {/* Publish button hidden if offer price exists */}
                {!leg.isCompleted && (
                  <button
                    className="btn btn-success mt-3"
                    onClick={() => handlePublish(leg, index)}
                  >
                    Publish
                  </button>
                )}
              </div>
            ))}
          </div>

          {/* Footer */}
          <div className="modal-footer">
            <button className="btn btn-secondary" onClick={onClose}>
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
