import React, { useState } from "react";
import {
  useDeleteBookingMutation,
  useGetBookingsQuery,
} from "../../redux/api/bookingApi";
import { Link, useSearchParams } from "react-router-dom";
import AddBookingForm from "./AddBookingForm";
import toast from "react-hot-toast";
import AddPaymentForm from "./AddPaymentForm";
import CustomPagination from "../layout/CustomPagination";

const BookingList = () => {
  const [showForm, setShowForm] = useState(false);
  const [editBooking, setEditBooking] = useState(null);
  const [selectedBooking, setSelectedBooking] = useState(null); // For payment
  const [showPayment, setShowPayment] = useState(false);
  const sortFieldMap = {
    id: "_id",
    traveler: "user.name",
    email: "user.email",
    mobile: "user.mobile",
    fromTo: "fromPlace", // you could extend this later
    dateTime: "travelDate", // or maybe "travelDate,travelTime"
    status: "status",
  };

  const [searchTerm, setSearchTerm] = useState("");
  const [searchParams, setSearchParams] = useSearchParams();
  const page = searchParams.get("page") || 1;
  const search = searchParams.get("search") || "";

  const params = { page: Number(page) };
  if (search) params.search = search;
  if (searchParams.get("sort")) {
    params.sort = searchParams.get("sort");
  }

  const { data, isLoading, refetch } = useGetBookingsQuery(params);
  const bookings = data?.bookings || [];
  console.log("Bookings:", bookings);
  console.log("Bookings data:", data?.resPerPage, data?.filteredBookingsCount);
  const [deleteBooking] = useDeleteBookingMutation();

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this booking?")) {
      try {
        await deleteBooking(id).unwrap();
        toast.success("Booking deleted");
        refetch();
      } catch (err) {
        toast.error("Failed to delete booking");
      }
    }
  };

  const handleEdit = (booking) => {
    setEditBooking(booking);
    setShowForm(true);
    setShowPayment(false);
  };

  const handleCloseForm = () => {
    setShowForm(false);
    setEditBooking(null);
  };

  const handlePaymentClick = (booking) => {
    setSelectedBooking(booking);
    setShowForm(false);
    setShowPayment(true);
    setTimeout(() => {
      const paymentForm = document.getElementById("payment-form");
      if (paymentForm) {
        paymentForm.scrollIntoView({ behavior: "smooth", block: "start" });
      }
    }, 100);
  };

  const handleSortChange = (columnKey) => {
    const field = sortFieldMap[columnKey];
    const currentSort = searchParams.get("sort") || "";
    let newSort;

    if (currentSort === field) {
      newSort = `-${field}`; // toggle to descending
    } else if (currentSort === `-${field}`) {
      newSort = field; // toggle to ascending
    } else {
      newSort = field;
    }

    searchParams.set("sort", newSort);
    searchParams.set("page", 1); // reset page
    setSearchParams(searchParams); // this triggers rerender
  };

  const handleSearch = (e) => {
    e.preventDefault();
    const params = new URLSearchParams(searchParams);
    if (searchTerm.trim()) {
      params.set("search", searchTerm.trim());
      params.set("page", 1);
    } else {
      params.delete("search");
    }
    setSearchParams(params);
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>Bookings</h2>
        <button
          className="btn btn-primary"
          onClick={() => {
            setEditBooking(null); // reset to add mode
            setShowForm(!showForm);
            setShowPayment(false);
          }}
        >
          {showForm ? "Close Form" : "Add Booking"}
        </button>
      </div>

      {showForm && (
        <AddBookingForm
          setShowForm={handleCloseForm}
          refetch={refetch}
          booking={editBooking} // send selected booking if edit
        />
      )}

      {showPayment && selectedBooking && (
        <AddPaymentForm
          booking={selectedBooking}
          refetch={refetch}
          onClose={() => setShowPayment(false)}
        />
      )}

      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <div className="table-responsive">
          <form onSubmit={handleSearch} className="mb-3 d-flex gap-2">
            <input
              type="text"
              className="form-control"
              placeholder="Search bookings..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <button type="submit" className="btn btn-primary">
              Search
            </button>
          </form>
          <table className="table mt-4">
            <thead>
              <tr>
                <th onClick={() => handleSortChange("id")}>
                  ID {searchParams.get("sort") === "id" && "▼"}
                </th>
                <th onClick={() => handleSortChange("traveler")}>
                  Traveler{""}
                  {searchParams.get("sort") === "user.name" && "▲"}
                  {searchParams.get("sort") === "user.name" && "▼"}
                </th>
                <th onClick={() => handleSortChange("email")}>
                  Email{""}
                  {searchParams.get("sort") === "user.email" && "▲"}
                  {searchParams.get("sort") === "user.email" && "▼"}
                </th>
                <th onClick={() => handleSortChange("mobile")}>
                  Mobile{""}
                  {searchParams.get("sort") === "user.mobile" && "▲"}
                  {searchParams.get("sort") === "user.mobile" && "▼"}
                </th>
                <th onClick={() => handleSortChange("fromTo")}>
                  From–To {searchParams.get("sort") === "fromTo" && "▼"}
                </th>
                <th onClick={() => handleSortChange("dateTime")}>
                  Date/Time{""}
                  {searchParams.get("sort") === "travelDate" && "▲"}
                  {searchParams.get("sort") === "travelDate" && "▼"}
                </th>
                <th onClick={() => handleSortChange("status")}>
                  Status{""}
                  {searchParams.get("sort") === "status" && "▲"}
                  {searchParams.get("sort") === "status" && "▼"}
                </th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {bookings?.map((b) => (
                <tr key={b._id}>
                  <td>{b._id.slice(-5)}</td>
                  <td>{b.user?.name}</td>
                  <td>{b.user?.email}</td>
                  <td>{b.user?.mobile}</td>
                  <td>
                    {b.fromPlace} → {b.toPlace}
                  </td>
                  <td>
                    {b.travelDate} {b.travelTime}
                  </td>
                  <td>
                    <span
                      className={`badge bg-${
                        b.status === "confirmed"
                          ? "success"
                          : b.status === "cancelled"
                          ? "danger"
                          : "warning"
                      }`}
                    >
                      {b.status}
                    </span>
                  </td>
                  <td>
                    <div className="dropdown">
                      <button
                        className="btn btn-sm btn-secondary dropdown-toggle"
                        type="button"
                        data-bs-toggle="dropdown"
                        aria-expanded="false"
                      >
                        Actions
                      </button>
                      <ul className="dropdown-menu">
                        <li>
                          <Link
                            to={`/admin/bookings/${b._id}`}
                            className="dropdown-item"
                          >
                            View
                          </Link>
                        </li>
                        <li>
                          <button
                            className="dropdown-item"
                            onClick={() => handleEdit(b)}
                          >
                            Edit
                          </button>
                        </li>
                        <li>
                          <button
                            className="dropdown-item"
                            onClick={() => handleDelete(b._id)}
                          >
                            Delete
                          </button>
                        </li>
                        <li>
                          <button
                            className="dropdown-item"
                            onClick={() => handlePaymentClick(b)}
                          >
                            Add Payment
                          </button>
                        </li>
                      </ul>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <CustomPagination
            resPerPage={data?.resPerPage || 10}
            totalResults={data?.filteredBookingsCount || 0}
          />
        </div>
      )}
    </div>
  );
};

export default BookingList;
