import React, { useEffect } from "react";
import { BrowserRouter as Router } from "react-router-dom"; // Remove Routes/Route/useLocation imports if not needed here
import { Toaster } from "react-hot-toast";

// ... (other imports unchanged)

import { useLazyGetProfileQuery } from "./redux/api/authApi"; // Change to useLazyGetProfileQuery
import { useDispatch, useSelector } from "react-redux"; // Add useSelector
import { logout } from "./redux/slices/authSlice";

function App() {
  const dispatch = useDispatch();
  const [getProfile, { data, isLoading, isSuccess, isError, error }] = useLazyGetProfileQuery(); // Now lazy

  // New: Get auth state for additional checks if needed
  const { isAuthenticated } = useSelector((state) => state.auth);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      getProfile(); // Trigger /me manually if token exists
    } else {
      dispatch(logout()); // Ensure clean state if no token
    }
  }, [dispatch, getProfile]); // Dependencies: Only run once on mount

  useEffect(() => {
    if (isError) {
      console.error("Profile fetch error:", error); // Log for debugging
      dispatch(logout());
    }
  }, [isError, error, dispatch]); // Only react to errors

  // Wait for auth resolution before rendering router/layout
  if (isLoading) {
    return <div>Loading authentication...</div>; // Replace with your spinner component
  }

  return (
    <>
      <Toaster position="top-center" reverseOrder={false} />
      <Router>
        <Layout />
      </Router>
    </>
  );
}

export default App;
