// components/pilots/PilotForm.jsx
import React, { useEffect, useState } from "react";
import {
  useCreatePilotMutation,
  useUpdatePilotMutation,
} from "../../redux/api/pilotApi";
import { useGetAirportsQuery } from "../../redux/api/airportApi"; // you already have this for flight type form
import toast from "react-hot-toast";

const PilotForm = ({ editingPilot, setShowForm, refetch }) => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    mobile: "",
    licenseNumber: "",
    experienceYears: "",
    baseAirport: "",
    rating: "",
    status: "active",
    photo: null,
  });

  const [preview, setPreview] = useState(null);

  const { data: airportsData, isLoading: airportsLoading } =
    useGetAirportsQuery();
  const [createPilot] = useCreatePilotMutation();
  const [updatePilot] = useUpdatePilotMutation();

  useEffect(() => {
    if (editingPilot) {
      setForm({
        name: editingPilot.name || "",
        email: editingPilot.email || "",
        mobile: editingPilot.mobile || "",
        licenseNumber: editingPilot.licenseNumber || "",
        experienceYears: editingPilot.experienceYears ?? "",
        baseAirport: editingPilot.baseAirport?._id || "",
        rating: editingPilot.rating ?? "",
        status: editingPilot.status || "active",
        photo: null,
      });
      setPreview(editingPilot.photo?.url || null);
    } else {
      setPreview(null);
    }
  }, [editingPilot]);

  const handleChange = (e) => {
    const { name, value, type, files } = e.target;
    if (type === "file") {
      const file = files?.[0] || null;
      setForm((p) => ({ ...p, photo: file }));
      setPreview(file ? URL.createObjectURL(file) : null);
    } else {
      setForm((p) => ({ ...p, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.name || !form.licenseNumber) {
      toast.error("Name and License Number are required");
      return;
    }

    const fd = new FormData();
    fd.append("name", form.name);
    fd.append("email", form.email);
    fd.append("mobile", form.mobile);
    fd.append("licenseNumber", form.licenseNumber);
    fd.append("experienceYears", form.experienceYears || 0);
    fd.append("baseAirport", form.baseAirport || "");
    fd.append("rating", form.rating || 0);
    fd.append("status", form.status || "active");
    if (form.photo) {
      fd.append("photo", form.photo);
    }

    try {
      if (editingPilot?._id) {
        await updatePilot({ id: editingPilot._id, formData: fd }).unwrap();
        toast.success("Pilot updated");
      } else {
        await createPilot(fd).unwrap();
        toast.success("Pilot created");
      }
      setShowForm(false);
      refetch();
    } catch (err) {
      console.error(err);
      toast.error("Failed to save pilot");
    }
  };

  return (
    <form
      className="border p-3 mb-4 bg-light"
      onSubmit={handleSubmit}
      encType="multipart/form-data"
    >
      <div className="row g-3">
        <div className="col-md-4">
          <input
            className="form-control"
            name="name"
            placeholder="Pilot Name"
            value={form.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="col-md-4">
          <input
            className="form-control"
            name="licenseNumber"
            placeholder="License Number"
            value={form.licenseNumber}
            onChange={handleChange}
            required
          />
        </div>

        <div className="col-md-4">
          <input
            className="form-control"
            name="email"
            placeholder="Email"
            value={form.email}
            onChange={handleChange}
            type="email"
          />
        </div>

        <div className="col-md-4">
          <input
            className="form-control"
            name="mobile"
            placeholder="Mobile"
            value={form.mobile}
            onChange={handleChange}
          />
        </div>

        <div className="col-md-4">
          <input
            type="number"
            className="form-control"
            name="experienceYears"
            placeholder="Experience (Years)"
            value={form.experienceYears}
            onChange={handleChange}
            min="0"
          />
        </div>

        <div className="col-md-4">
          <select
            className="form-control"
            name="baseAirport"
            value={form.baseAirport}
            onChange={handleChange}
          >
            <option value="">Select Base Airport</option>
            {!airportsLoading &&
              airportsData?.airports?.map((a) => (
                <option key={a._id} value={a._id}>
                  {a.name} ({a.code})
                </option>
              ))}
          </select>
        </div>

        <div className="col-md-4">
          <input
            type="number"
            className="form-control"
            name="rating"
            placeholder="Rating (0-5)"
            value={form.rating}
            onChange={handleChange}
            min="0"
            max="5"
            step="0.1"
          />
        </div>

        <div className="col-md-4">
          <select
            className="form-control"
            name="status"
            value={form.status}
            onChange={handleChange}
          >
            <option value="active">Active</option>
            <option value="inactive">Inactive</option>
          </select>
        </div>

        <div className="col-md-4">
          <label className="form-label">Pilot Photo</label>
          <input
            type="file"
            name="photo"
            className="form-control"
            accept="image/*"
            onChange={handleChange}
          />
        </div>

        {preview && (
          <div className="col-md-2 d-flex align-items-center">
            <img
              src={preview}
              alt="Preview"
              width="80"
              height="80"
              style={{ objectFit: "cover", borderRadius: 8 }}
            />
          </div>
        )}

        <div className="col-md-12 d-flex gap-2 mt-2">
          <button className="btn btn-success" type="submit">
            {editingPilot ? "Update" : "Add"} Pilot
          </button>
          <button
            className="btn btn-secondary"
            type="button"
            onClick={() => setShowForm(false)}
          >
            Cancel
          </button>
        </div>
      </div>
    </form>
  );
};

export default PilotForm;
