import React, { useState } from "react";
import DataTable from "react-data-table-component";
import {
  useGetFlightCategoriesQuery,
  useDeleteFlightCategoryMutation,
} from "../../redux/api/flightCategoryApi";
import FlightCategoryForm from "./FlightCategoryForm";
import toast from "react-hot-toast";

const FlightCategoryList = () => {
  const { data, isLoading, refetch } = useGetFlightCategoriesQuery();
  const [deleteCategory] = useDeleteFlightCategoryMutation();
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [editData, setEditData] = useState(null);

  const handleAdd = () => {
    setEditData(null);
    setIsFormOpen(true);
  };

  const handleEdit = (row) => {
    setEditData(row);
    setIsFormOpen(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this category?")) {
      try {
        await deleteCategory(id).unwrap();
        toast.success("Flight Category deleted successfully");
        refetch();
      } catch (err) {
        toast.error(err?.data?.message || "Delete failed");
      }
    }
  };

  const columns = [
    { name: "Name", selector: (row) => row.name, sortable: true },
    {
      name: "Image",
      cell: (row) =>
        row.image ? (
          <img
            src={row.image}
            alt="Category"
            width={60}
            style={{ borderRadius: "5px" }}
          />
        ) : (
          "No Image"
        ),
    },
    {
      name: "Actions",
      cell: (row) => (
        <>
          <button
            onClick={() => handleEdit(row)}
            className="btn btn-sm btn-primary me-2"
          >
            Edit
          </button>
          <button
            onClick={() => handleDelete(row._id)}
            className="btn btn-sm btn-danger"
          >
            Delete
          </button>
        </>
      ),
    },
  ];

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>Flight Categories</h2>
        <button onClick={handleAdd} className="btn btn-success">
          Add Category
        </button>
      </div>

      {isFormOpen && (
        <FlightCategoryForm
          editData={editData}
          closeForm={() => setIsFormOpen(false)}
          refetch={refetch}
        />
      )}

      <DataTable
        columns={columns}
        data={data || []}
        progressPending={isLoading}
        pagination
        highlightOnHover
        striped
      />
    </div>
  );
};

export default FlightCategoryList;
